= M.STK_CTGRY_ID JOIN SAL0046D O ON O.INSTALL_ENTRY_ID = (SELECT MIN(SAL0046D.INSTALL_ENTRY_ID) FROM SAL0046D WHERE SAL0046D.SALES_ORD_ID = C.SALES_ORD_ID AND SAL0046D.STUS_CODE_ID = '4' GROUP BY SAL0046D.SALES_ORD_ID) AND O.SALES_ORD_ID = C.SALES_ORD_ID JOIN SAL0047D P ON O.INSTALL_ENTRY_ID = P.ENTRY_ID WHERE 1 = 1
