mapper.select:

      SELECT 
			     (SELECT
			         COUNT(AREA_ID) AS   ACNT
			        FROM SYS0064M  WHERE AREA_ID =#{areaId}
			     ) ACNT ,
			     (SELECT
			         COUNT(AREA_ID) AS   BCNT
			        FROM SYS0064M  WHERE AREA_ID =#{areaId}
			     ) BCNT ,
			     (SELECT
			         COUNT(AREA_ID) AS   UCNT
			        FROM SYS0064M  WHERE AREA_ID =#{areaId}
			     ) UCNT 
	   FROM DUAL


