mapper.select:
SELECT DISTINCT
A.SO_EXCHG_ID AS SO_EXCHG_ID, A.SO_ID AS SO_ID, L.TEST_RESULT_STUS AS TEST_RESULT_STUS, L.TEST_RESULT_ID AS TEST_RESULT_ID, L.RCD_TMS AS RCD_TMS, B.SALES_ORD_NO AS ORDER_NO, N.CODE AS PEX_TEST_RESULT_STUS, O.CODE_NAME AS PRODUCT_CATEGORY, D.STK_CODE AS PRODUCT_CODE, D.STK_DESC AS PRODUCT_NAME, E.LAST_INSTALL_SERIAL_NO AS PROD_SERIAL_NO, E.FIRST_INSTALL_DT AS PEX_DATE, NVL(FLOOR(MONTHS_BETWEEN(A.SO_EXCHG_CRT_DT, TO_DATE(E.FIRST_INSTALL_DT,'DD/MM/YYYY'))),'0') AS AS_AGING , E.LAST_INSTALL_CT_CODE AS CT_CODE, E.LAST_INSTALL_CT_NAME AS CT_NAME, E.DSC_CODE, E.DSC_NAME, G.DEFECT_DESC AS Problem_Symptom_Large , H.DEFECT_DESC AS Problem_Symptom_Small , I.DEFECT_DESC AS Defect_Part_Large , I.DEFECT_RMK AS Defect_Part_Small , J.CODE AS PEX_REASON_CODE, J.RESN_DESC AS PEX_REASON_DESC, K.SVC_NO AS RET_NO, C.INSTALL_ENTRY_NO AS PEX_INS_NO, L.TEST_RESULT_NO, M.NAME AS CUST_NAME , L.TEST_RESULT_REM , L.TEST_SETTLE_DT, L.AMP, L.VOLTAGE, E.INST_STATE, E.INST_CITY, E.INST_AREA, L.PROD_GENUINE, NVL((SELECT USER_NAME FROM SYS0047M WHERE USER_ID = L.CRT_USER_ID),L.CRT_USER_ID) CRT_USER_ID, L.CRT_DT, NVL((SELECT USER_NAME FROM SYS0047M WHERE USER_ID = L.UPD_USER_ID),L.UPD_USER_ID) UPD_USER_ID, L.UPD_DT
FROM SAL0004D A JOIN SAL0001D B ON B.SALES_ORD_ID = A.SO_ID JOIN SAL0046D C ON C.INSTALL_ENTRY_ID = A.INSTALL_ENTRY_ID LEFT JOIN SYS0026M D ON D.STK_ID = C.INSTALL_STK_ID LEFT JOIN SAL1010V E ON E.ORD_ID = A.SO_ID LEFT JOIN SAL0265D F ON F.SO_EXCHG_ID = A.SO_EXCHG_ID LEFT JOIN SYS0032M J ON J.RESN_ID = A.SO_EXCHG_RESN_ID LEFT JOIN SVC0026T K ON K.SALES_ORD_ID = A.SO_ID JOIN SVC0125D L ON L.SO_EXCHG_ID = A.SO_EXCHG_ID LEFT JOIN SYS0100M G ON G.DEFECT_ID = L.PROB_LARGE_ID AND G.DEFECT_TYP = 'DD' LEFT JOIN SYS0100M H ON H.DEFECT_ID = L.PROB_SMALL_ID AND H.DEFECT_TYP = 'DC' LEFT JOIN SYS0100M I ON I.DEFECT_ID = L.DEF_LARGE_ID AND I.DEFECT_TYP = 'DP' LEFT JOIN SAL0029D M ON M.CUST_ID = B.CUST_ID LEFT JOIN SYS0038M N ON N.STUS_CODE_ID = L.TEST_RESULT_STUS LEFT JOIN SYS0013M O ON O.CODE_ID = D.STK_CTGRY_ID AND O.CODE_MASTER_ID = 11 LEFT JOIN SYS0047M P ON P.USER_ID = L.CRT_USER_ID AND P.USER_ID = L.UPD_USER_ID
WHERE 1=1 AND L.TEST_RESULT_STUS != '8'
AND L.TEST_RESULT_STUS IN
#{item}
AND E.DSC_CODE IN
#{item}
AND D.STK_CTGRY_ID IN
#{item}
AND D.STK_ID IN
#{item}
AND ( TO_CHAR(L.TEST_SETTLE_DT, 'YYYYMMDD') >= TO_CHAR(TO_DATE( #{settleDtFrm},'dd/mm/yyyy'), 'YYYYMMDD') )
AND ( TO_CHAR(L.TEST_SETTLE_DT, 'YYYYMMDD') <= TO_CHAR(TO_DATE( #{settleDtTo},'dd/mm/yyyy'), 'YYYYMMDD') )
AND B.SALES_ORD_NO = #{orderNum}
AND L.TEST_RESULT_NO = #{TestResultNo}
ORDER BY L.TEST_RESULT_NO

SELECT COUNT(*) COUNT
  FROM SVC0125D
  WHERE TEST_RESULT_ID = #{testResultId}
    AND TEST_RESULT_NO = #{testResultNo}
    AND SO_EXCHG_ID = #{soExchgId}
    AND RCD_TMS = #{rcdTms}

mapper.update:
UPDATE SVC0125D
SET RCD_TMS = TO_CHAR(SYSTIMESTAMP,'ddmmyyyyHH24miss')
WHERE TEST_RESULT_ID = #{testResultId}
  AND TEST_RESULT_NO = #{testResultNo}
  AND RCD_TMS = #{rcdTms}

UPDATE SVC0125D
SET TEST_RESULT_STUS = #{TEST_RESULT_STUS}
 , TEST_SETTLE_DT = TO_DATE(#{TEST_SETTLE_DT}, 'DD/MM/YYYY')
 , TEST_RESULT_REM = #{TEST_RESULT_REM}
 , AMP = #{AMP}
 , VOLTAGE = #{VOLTAGE}
 , PROD_GENUINE = #{PROD_GENUINE}
 , TEST_SETTLE_TIME = #{TEST_SETTLE_TIME}
 , DEF_LARGE_ID = #{PEX_DEFECT_PART_ID}
 , PROB_LARGE_ID = #{PEX_DEFECT_DTL_RESN_ID}
 , PROB_SMALL_ID = #{PEX_DEFECT_ID}
 , UPD_DT = SYSDATE
 , UPD_USER_ID = #{updator}
WHERE TEST_RESULT_ID = #{TEST_RESULT_ID}
