SELECT
DISTINCT B.SALES_ORD_NO, B.SALES_ORD_ID, B.SALES_ORD_ID AS ORD_ID, NVL(FLOOR(MONTHS_BETWEEN(C.AS_SETL_DT,IR2.INSTALL_DT)), '0') AS AS_AGING , DECODE (IR2.INSTALL_DT, NULL, '01/01/1900', TO_CHAR (IR2.INSTALL_DT, 'DD/MM/YYYY')) LAST_INSTALL_DT, C.AS_SETL_DT, Q.CODE_NAME AS PRODUCT_CATEGORY, P.STK_CODE, P.STK_DESC, A.AS_NO, A.AS_ID, C.AS_RESULT_NO, G.NAME AS CUST_NAME, NVL (IR2.SERIAL_NO, ' ') LAST_INSTALL_SERIAL_NO, R.TEST_UP_CT, B.CODE DSC_CODE, B.NAME DSC_NAME, D.ASR_ITM_ID, D.ASR_ITM_PART_ID, D.ASR_ITM_PART_DESC, W.STK_CODE AS ASR_ITM_PART_CODE, IR2.PSI, IR2.LPM, C.AS_RESULT_REM, M.DEFECT_DESC AS AS_ERROR_CODE, N.DEFECT_DESC AS AS_DESCRIPTION, DT.DEFECT_DESC AS SOLUTION_LARGE , SC.DEFECT_DESC AS SOLUTION_SMALL , DD.DEFECT_DESC AS PROBLEM_SYMPTOM_LARGE , DC.DEFECT_DESC AS PROBLEM_SYMPTOM_SMALL , DP.DEFECT_DESC AS DEFECT_PART_LARGE , DP.DEFECT_RMK AS DEFECT_PART_SMALL , MA.STATE INST_STATE, MA.CITY INST_CITY, MA.AREA INST_AREA, R.TEST_UP_ID AS TEST_RESULT_ID, R.TEST_UP_NO AS TEST_RESULT_NO, R.TEST_UP_REM AS TEST_RESULT_REMARK, R.TEST_UP_GNE, TO_CHAR(R.TEST_UP_SETL_DT, 'DD/MM/YYYY') AS TEST_UP_SETL_DT, R.TEST_YN, S.CMPLT_YN, R.RCD_TMS, TO_CHAR(R.TEST_MNF_DT, 'DD/MM/YYYY') AS TEST_MNF_DT, CASE WHEN R.TEST_UP_STUS = 0 THEN CASE WHEN R.TEST_UP_NO IS NULL THEN 'Active' END ELSE T.NAME END AS TEST_STUS, ASDD.DEFECT_DESC AS_PROBLEM_SYMPTOM_LARGE, ASDC.DEFECT_DESC AS AS_PROBLEM_SYMPTOM_SMALL
FROM SVC0004D C JOIN SVC0001D A ON A.AS_ID = C.AS_ENTRY_ID JOIN SAL0001D B ON B.SALES_ORD_ID = A.AS_SO_ID
JOIN SAL0047D IR2 ON (IR2.RESULT_ID,IR2.entry_id) = (SELECT MAX(x.install_result_id), MAX(X.INSTALL_ENTRY_ID) entry_id FROM SAL0046D x WHERE x.STUS_CODE_ID = 4 AND x.SALES_ORD_ID = B.sales_ORD_ID) JOIN SVC0005D D ON C.AS_RESULT_ID = D.AS_RESULT_ID AND D.ASR_ITM_PART_ID IS NOT NULL JOIN SAL0029D G ON G.CUST_ID = B.CUST_ID LEFT JOIN SVC0122D R ON R.ASR_ITM_ID = D.ASR_ITM_ID JOIN SYS0100M H1 ON H1.DEFECT_ID = C.AS_DEFECT_TYPE_ID AND H1.DEFECT_TYP = 'DT' AND H1.DEFECT_CODE IN ('1800', '1500') LEFT JOIN SYS0100M M ON M.DEFECT_ID = A.AS_MALFUNC_ID LEFT JOIN SYS0100M N ON N.DEFECT_ID = A.AS_MALFUNC_RESN_ID AND N.DEFECT_GRP = M.DEFECT_ID JOIN SAL0002D O ON O.SALES_ORD_ID = B.SALES_ORD_ID JOIN SYS0026M P ON P.STK_ID = O.ITM_STK_ID LEFT JOIN SYS0013M Q ON Q.CODE_ID = P.STK_CTGRY_ID JOIN LOG0103M S ON S.SVC_ORD_ID = A.AS_NO AND S.CMPLT_YN = 'Y' LEFT JOIN SYS0038M T ON T.STUS_CODE_ID = R.TEST_UP_STUS LEFT JOIN SYS0026M W ON W.STK_ID = D.ASR_ITM_PART_ID JOIN SAL0045D I ON I.SALES_ORD_ID = B.SALES_ORD_ID JOIN SAL0023D CAL ON CAL.CUST_ADD_ID = I.ADD_ID JOIN SYS0005M B ON B.BRNCH_ID = I.BRNCH_ID JOIN SYS0064M MA ON MA.AREA_ID = CAL.AREA_ID LEFT JOIN SYS0100M DT ON R.TEST_AS_DEFECT_TYPE_ID =ã€€DT.DEFECT_ID AND DT.DEFECT_TYP ='DT' LEFT JOIN SYS0100M SC ON R.TEST_AS_SLUTN_RESN_ID = SC.DEFECT_ID AND SC.DEFECT_TYP ='SC' LEFT JOIN SYS0100M DD ON R.TEST_AS_DEFECT_DTL_RESN_ID = DD.DEFECT_ID AND DD.DEFECT_TYP ='DD' LEFT JOIN SYS0100M DC ON R.TEST_AS_DEFECT_ID = DC.DEFECT_ID AND DC.DEFECT_TYP ='DC' LEFT JOIN SYS0100M DP ON R.TEST_AS_DEFECT_PART_ID = DP.DEFECT_ID AND DP.DEFECT_TYP ='DP' LEFT JOIN SYS0100M ASDD ON C.AS_DEFECT_DTL_RESN_ID = ASDD.DEFECT_ID AND ASDD.DEFECT_TYP ='DD' LEFT JOIN SYS0100M ASDC ON C.AS_DEFECT_ID = ASDC.DEFECT_ID AND ASDC.DEFECT_TYP ='DC'
WHERE 1 = 1
AND B.SALES_ORD_NO = #{orderNum}
AND B.CODE = #{cmbbranchId}
AND (R.TEST_UP_STUS = '1' OR R.TEST_UP_STUS ='' OR R.TEST_UP_STUS IS NULL)
AND R.TEST_UP_STUS = '4'
AND P.STK_CTGRY_ID = #{cmbCategory}
AND P.STK_CODE = #{asProduct}
AND D.ASR_ITM_PART_ID = #{spareFilterName}
AND R.TEST_UP_GNE = #{Genuinity}
AND W.STK_TYPE_ID = #{Type}
AND R.TEST_UP_NO = #{TestResultNo}
AND C.AS_SETL_DT BETWEEN TO_DATE(#{asStartDt}, 'YYYY-MM-DD') AND TO_DATE(#{asEndDt}||' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')


