mapper.select:
SELECT DISTINCT CR.CUST_CRC_TOKEN , C.NRIC
FROM SAL0028D CR LEFT JOIN SAL0029D C ON C.CUST_ID = CR.CUST_ID AND NRIC = #{nric} LEFT JOIN SAL0074D R ON R.CUST_CRC_ID = CR.CUST_CRC_ID WHERE CUST_CRC_TOKEN = #{cardTokenId}
AND r.is_3rd_party = 1

mapper.update:
UPDATE API0005D
SET
    SALES_ORD_ID = #{salesOrdId}
  , STUS_CODE_ID = 4
WHERE ECOMM_ORD_ID = #{ecommOrdId}

mapper.select:
      SELECT count(*) FROM API0005D
	  WHERE ref_no = #{refNo}
	  AND BNDL_ID = #{bndlId}
	  AND STUS_CODE_ID in (1,4)
