mapper.select:
SELECT MEM_ID ,MEM_CODE ,FULL_NAME ,NRIC ,MEM_TYPE FROM ORG0001D WHERE 1= 1
AND MEM_CODE = #{memCode}
AND STUS = 1

SELECT MEM_ID,MEM_CODE,FULL_NAME,NRIC,MEM_TYPE, COURSE.* FROM ORG0001D O LEFT JOIN (SELECT D.COURS_ID, COURS_CODE, COURS_NAME, COURS_LOC, COURS_STUS_ID, COURS_MEM_TYPE_ID, COURS_ITM_ID, COURS_MEM_ID, COURS_TEST_RESULT, COURS_MEM_STUS_ID, COURS_D_MEM_NAME, COURS_D_MEM_NRIC, COURS_CDP_POINT FROM MSC0008D D JOIN MSC0009D C ON D.COURS_ID = C.COURS_ID WHERE COURS_TEST_RESULT IS NULL
and COURS_MEM_STUS_ID = #{coursMemStatus}
) COURSE ON O.MEM_ID = COURSE.COURS_MEM_ID WHERE 1=1
AND O.MEM_CODE = #{memCode}

SELECT C.COURS_ID, C.COURS_ITM_ID, C.COURS_MEM_ID, O.MEM_CODE, C.COURS_MEM_STUS_ID, CM.COURS_CODE
FROM MSC0009D C
    JOIN MSC0008D CM ON C.COURS_ID = CM.COURS_ID
    JOIN ORG0001D O ON O.MEM_ID = C.COURS_MEM_ID
WHERE 1 = 1


    AND CM.COURS_CODE = #{coursCode}



    AND C.COURS_MEM_ID = #{memId}



    AND O.MEM_CODE = #{memCode}

mapper.update:
MERGE INTO MSC0009D USING DUAL ON (COURS_ID = #{coursId} AND COURS_MEM_ID = #{coursMemId}) WHEN MATCHED THEN UPDATE SET COURS_MEM_STUS_ID = 1
, COURS_MEM_SHIRT_SIZE = #{coursMemShirtSize}
WHEN NOT MATCHED THEN INSERT ( COURS_ITM_ID, COURS_ID, COURS_MEM_ID, COURS_MEM_STUS_ID, COURS_D_UPD_USER_ID, COURS_D_UPD_DT, COURS_D_MEM_NAME, COURS_D_MEM_NRIC, COURS_MEM_SHIRT_SIZE ) VALUES ( MSC0009D_COURS_ITM_ID_SEQ.NEXTVAL , #{coursId} , #{coursMemId} , 1 , #{userId} , SYSDATE , #{coursDMemName} , #{coursDMemNric} , #{coursMemShirtSize} )

/* trainingMapper.updateAttendee */
UPDATE MSC0009D
SET
    COURS_D_UPD_USER_ID = #{userId}
   ,COURS_D_UPD_DT = SYSDATE



   ,COURS_MEM_STUS_ID = #{coursMemStusId}





   ,COURS_TEST_RESULT = #{coursTestResult}



   ,COURS_CDP_POINT = #{coursCdpPoint}


       , COURS_ATTEND_DAY = #{coursAttendDay}


   , COURS_MEM_SHIRT_SIZE = #{shirtSize}


WHERE 1 = 1

AND COURS_ID = #{coursId}
AND COURS_MEM_ID = #{coursMemId}
