mapper.insert:
INSERT INTO ATD0002D ( ATD_ID , BATCH_ID , ATD_TYPE , MEM_CODE , MANAGER_CODE , DT_FROM , DT_TO , TIME , DISAB , CRT_USER_ID , CRT_DT ) SELECT ATD0002D_ATD_ID_SEQ.nextval, #{batchId}, atdType, memCode, managerCode, dateFrom, dateTo, time ,disab, crtUserId, crtDt FROM (
SELECT #{item.atdType} atdType , #{item.memCode} memCode , #{item.managerCode} managerCode , TO_DATE(#{item.dateFrom}, 'DD/MM/RRRR') dateFrom , TO_DATE(#{item.dateTo}, 'DD/MM/RRRR') dateTo , TO_DATE(#{item.time}, 'HH:MI.SS PM') time , '0' disab , #{item.crtUserId} crtUserId , SYSDATE crtDt FROM DUAL
)

mapper.update:
 UPDATE ATD0001M
 SET
          BATCH_STUS_ID = 8
        , UPD_USER_ID = #{crtUserId}
        , UPD_DT =SYSDATE
WHERE BATCH_MTH_YEAR = #{batchMthYear}

 UPDATE ATD0001M
 SET
          BATCH_STUS_ID = 8
        , UPD_USER_ID = #{crtUserId}
        , UPD_DT =SYSDATE
WHERE BATCH_ID = #{batchId}

 UPDATE ATD0002D
 SET
          DISAB = 1
        , UPD_USER_ID = #{crtUserId}
        , UPD_DT =SYSDATE
WHERE BATCH_ID = #{batchId}

 UPDATE ATD0001M
 SET
          BATCH_STUS_ID = 4
        , UPD_USER_ID = #{crtUserId}
        , UPD_DT =SYSDATE
WHERE BATCH_ID = #{batchId}

mapper.select:
{
      call SP_ATD_RATE_CALCULATION(#{batchId},#{p1 , mode=OUT , jdbcType=VARCHAR , javaType=String , resultMap=atdRate})
}
