mapper.select:
SELECT ORG_CD , ORG_NM FROM CMM0041C WHERE 1=1 AND START_YEARMONTH < END_YEARMONTH
AND ORG_GR_CD = #{orgRgCombo}
GROUP BY ORG_CD, ORG_NM ORDER BY ORG_CD

SELECT T1.CODE_MASTER_ID , T2.CODE_ID , substr(T2.CODE,0,7) code, T2.CODE_NAME , T2.CODE_DESC AS CDDS, SUBSTR(T2.CODE,9,9) NM FROM SYS0012M T1 JOIN SYS0013M T2 ON T1.CODE_MASTER_ID = T2.CODE_MASTER_ID WHERE 1=1
AND T1.CODE_MASTER_ID=#{mstId}
AND CODE LIKE 'BSD%' ORDER BY NM

SELECT CAL_SEQ, CAL_CODE, CAL_NAME, CAL_STATE, CAL_START_TIME, CAL_CHECK_LAST_YN, CRT_USER_ID, CRT_DT,CAL_YEAR_MONTH FROM CMM0053M WHERE 1=1
AND CAL_CODE = #{codeId}
AND CAL_CHECK_LAST_YN = 'Y' AND CAL_STATE= 1 ORDER BY CAL_START_TIME DESC

mapper.update:
        UPDATE CMM0053M SET  
			CAL_CHECK_LAST_YN = 'Y' ,
			CAL_END_TIME = sysdate ,

			CAL_ERROR_CODE =#{v_sqlcode},


			 CAL_ERROR_CONTENTS =#{v_sqlcont},

			CAL_STATE = #{state} 
        WHERE CAL_CODE = #{codeId}
            AND CAL_SEQ= #{calSeq}

mapper.select:
{
call  ${procedureNm}(${taskId},#{searchDt},${loginId},#{v_procname,mode=OUT,jdbcType=VARCHAR},#{v_result,mode=OUT,jdbcType=VARCHAR}, #{ v_sqlcode,mode=OUT,jdbcType=VARCHAR} , #{ v_sqlcont,mode=OUT,jdbcType=VARCHAR})
}

SELECT 
  count(*) cnt
FROM CMM0028D T1 JOIN ORG0001D T2 
      ON T1.EMPLY_ID = T2.MEM_ID
  JOIN SYS0013M T3
      ON T2.MEM_TYPE = T3.CODE_ID
  JOIN CMM0006T T4 
      ON T1.EMPLY_ID= T4.EMPLY_ID
          AND T4.COMM_TASK_ID = #{taskId}
WHERE T1.TASK_ID = #{taskId}
  AND T3.CODE = #{codeGruop}
  AND T4.EMPLY_LEV = #{emplyLev}

      and T1.EMPLY_ID LIKE '%'|| #{memberId} ||'%'

SELECT 
    count(*) cnt
FROM CMM0029D T1 JOIN ORG0001D T2 
        ON T1.EMPLY_ID = T2.MEM_ID
    JOIN SYS0013M T3
        ON T2.MEM_TYPE = T3.CODE_ID
    JOIN CMM0006T T4 
        ON T1.EMPLY_ID= T4.EMPLY_ID
             AND T4.COMM_TASK_ID = #{taskId}
WHERE T1.TASK_ID = #{taskId}
    AND T3.CODE = #{codeGruop}
    AND T4.EMPLY_LEV = #{emplyLev}

      AND T1.EMPLY_ID LIKE '%'|| #{memberId} ||'%'

        SELECT T1.TASK_ID, T1. EMPLY_ID,T1.RUN_ID,T2.MEM_TYPE MEM_TYPE,
            nvl(T1.R1,'0') r1,nvl(T1.R2,'0') r2,nvl(T1.R3,'0') r3,nvl(T1.R4,'0') r4,nvl(T1.R5,'0') r5,nvl(T1.R6,'0') r6,nvl(T1.R7 ,'0') r7,nvl(T1.R8 ,'0') r8
		FROM CMM0029D T1 JOIN ORG0001D T2 
		        ON T1.EMPLY_ID = T2.MEM_ID
		    JOIN SYS0013M T3
		        ON T2.MEM_TYPE = T3.CODE_ID
		    JOIN CMM0006T T4 
		        ON T1.EMPLY_ID= T4.EMPLY_ID
		             AND T4.COMM_TASK_ID = #{taskId}
		WHERE T1.TASK_ID = #{taskId}
		    AND T3.CODE = #{codeGruop}
		    AND T4.EMPLY_LEV = #{emplyLev}

              AND T1.EMPLY_ID LIKE '%'|| #{memberId} ||'%'

        SELECT COUNT(*) FROM(
	        SELECT T1.COMM_TASK_ID,T1.IS_EXCLUDE
	        FROM CMM0006T T1 JOIN ORG0001D T2 
	                ON T1.EMPLY_ID = T2.MEM_ID
	            JOIN SYS0013M T3
	                ON T2.MEM_TYPE = T3.CODE_ID
	         WHERE T1.COMM_TASK_ID = #{taskId} 

	              AND T3.CODE_ID= #{codeId}


	              AND T1.EMPLY_ID LIKE '%'|| #{memberId} ||'%'

            ) scr
            WHERE scr.COMM_TASK_ID = #{taskId}

              AND  scr.IS_EXCLUDE = #{useYnCombo}

SELECT * FROM( SELECT T1.DEPT_CODE, nvl(T1.MANGR_ID,'0') mangrId, T2.MEM_ID,T1.EMPLY_TYPE_CODE,TO_CHAR(T1.JOIN_DT,'yyyy-mm-dd')joinDt, T1.EMPLY_LEV,T1.EMPLY_LEV_AGE,T1.WORKING_MONTH,T1.SPONS_CODE,T1.EMPLY_STUS_ID, T1.EMPLY_STUS_CODE,T1.EMPLY_LEV_RANK,nvl(T1.EMPLY_VEH,'0') emplyVeh,T1.EMPLY_BIZ_TYPE_ID, nvl(T1.MANGR_CODE,'0') mangrCode, nvl(T1.EMPLY_CODE,'0') emplyCode, nvl(T1.PAY_STUS,'0') payStus, nvl(T1.IS_HSPTLZ,'0') isHsptlz, T1.RUN_ID,T1.COMM_TASK_ID taskId,nvl(T1.IS_EXCLUDE,'0') IS_EXCLUDE FROM CMM0006T T1 JOIN ORG0001D T2 ON T1.EMPLY_ID = T2.MEM_ID JOIN SYS0013M T3 ON T2.MEM_TYPE = T3.CODE_ID WHERE T1.COMM_TASK_ID = #{taskId}
AND T3.CODE_ID= #{codeId}
AND T1.EMPLY_ID LIKE '%'|| #{memberId} ||'%'
) scr WHERE scr.taskId = #{taskId}
AND scr.IS_EXCLUDE = #{useYnCombo}
ORDER BY scr.RUN_ID

        SELECT COUNT(*) cnt FROM(
	        SELECT  T1.TASK_ID,T1.IS_EXCLUDE
	        FROM CMM0007T T1 JOIN ORG0001D T2 
	                ON T1.SALES_EMPLY_ID = T2.MEM_ID
	            JOIN SYS0013M T3
	                ON T2.MEM_TYPE = T3.CODE_ID
	         WHERE T1.TASK_ID = #{taskId}  

	              AND T3.CODE_ID= #{codeId}


	              AND T1.SALES_EMPLY_ID LIKE '%'|| #{memberId} ||'%' 


	              AND T1.ORD_ID LIKE '%'|| #{ordId} ||'%' 

            ) scr
            WHERE scr.TASK_ID=#{taskId}  

              AND  scr.IS_EXCLUDE = #{useYnCombo}

        SELECT * FROM(
            SELECT 
	            T1.ORD_ID, T2.MEM_ID,T3.CODE,  T1.ORD_TYPE_ID, T1.PRODUCT_ID, T1.UNIT_VALU,
	            T1.PRC, T1.PV_VALU,T1.RUN_ID,T1.TASK_ID, nvl(T1.IS_EXCLUDE,'0') IS_EXCLUDE
			FROM CMM0007T T1 JOIN ORG0001D T2 
			        ON T1.SALES_EMPLY_ID = T2.MEM_ID
			    JOIN SYS0013M T3
			        ON T2.MEM_TYPE = T3.CODE_ID
			 WHERE T1.TASK_ID = #{taskId}  

	              AND T3.CODE_ID= #{codeId}


			      AND T1.SALES_EMPLY_ID LIKE '%'|| #{memberId} ||'%' 


			      AND T1.ORD_ID LIKE '%'|| #{ordId} ||'%' 

		    )scr
		    WHERE scr.TASK_ID=#{taskId}  

		      AND  scr.IS_EXCLUDE = #{useYnCombo} 

         ORDER BY scr.RUN_ID

