MERGE INTO CMM0006T t0 USING ( SELECT T1.RUN_ID FROM CMM0006T T1 JOIN ORG0001D T2 ON T1.EMPLY_ID = T2.MEM_ID JOIN SYS0013M T3 ON T2.MEM_TYPE = T3.CODE_ID WHERE T1.COMM_TASK_ID = #{taskId}
AND T3.CODE_ID= #{codeId}
AND T1.EMPLY_ID LIKE '%'|| #{memberId} ||'%'
AND IS_EXCLUDE = #{useYnCombo}
) src ON ( t0.RUN_ID = src.RUN_ID ) WHEN MATCHED THEN UPDATE SET t0.IS_EXCLUDE = 0

UPDATE CMM0006T SET IS_EXCLUDE = 1 
WHERE COMM_TASK_ID= #{taskid}
