mapper.select:
SELECT T2.CODE_MASTER_ID AS mstId , T1.CODE_MASTER_NAME AS mstNm, T2.CODE_ID AS cdId, T2.CODE AS cd, T2.CODE_NAME AS cdNm, T2.CODE_DESC AS cdDs FROM SYS0012M T1 JOIN SYS0013M T2 ON T1.CODE_MASTER_ID = T2.CODE_MASTER_ID WHERE 1=1
AND T1.CODE_MASTER_ID = #{mstId}
ORDER BY T2.CODE_ID

SELECT ORG_GR_CD, ORG_GR_NM, ORG_SEQ, ORG_CD, ORG_NM, USE_YN, CASE WHEN END_YEARMONTH IS NOT NULL AND LENGTH(END_YEARMONTH)=6 THEN SUBSTR(END_YEARMONTH,5) || '/' || SUBSTR(END_YEARMONTH,0,4) ELSE END_YEARMONTH END END_DT , T2.CODE_DESC AS cdDs, to_char(T1.UPD_DT,'yyyy-mm-dd hh24:mi:ss') UPD_DT FROM CMM0041C T1 JOIN SYS0013M T2 ON T1.ORG_CD = T2.CODE_ID WHERE 1=1
AND T1.ORG_GR_CD = #{orgRgCombo}
AND T1.ORG_CD = #{orgCombo}
AND T1.USE_YN = #{useYnCombo}
AND #{searchDt} BETWEEN START_YEARMONTH AND END_YEARMONTH
ORDER BY T1.ORG_GR_NM , T2.CODE_ID

mapper.update:
UPDATE CMM0041C
SET
END_YEARMONTH=(SELECT TO_CHAR(ADD_MONTHS(SYSDATE,-1),'YYYYMM') FROM DUAL)
, UPD_USER_ID=#{updUserId}
, UPD_DT=SYSDATE
WHERE ORG_GR_CD=#{orgGrCd} AND ORG_CD=#{orgCd} AND ORG_SEQ=#{orgSeq}

UPDATE CMM0041C
SET
USE_YN=#{useYn}
, UPD_USER_ID=#{updUserId}
, UPD_DT=SYSDATE
WHERE ORG_GR_CD=#{orgGrCd} AND ORG_CD=#{orgCd} AND ORG_SEQ=#{orgSeq}

mapper.select:
SELECT ORG_CD , ORG_NM FROM CMM0041C WHERE 1=1 AND START_YEARMONTH < END_YEARMONTH
AND ORG_GR_CD = #{orgGrCombo}
GROUP BY ORG_CD, ORG_NM ORDER BY ORG_NM

SELECT T1.ITEM_SEQ, T1.ITEM_CD, T1.ORG_SEQ, T1.ORG_GR_CD, T1.ORG_CD, (SELECT CODE_DESC FROM SYS0013M WHERE CODE_ID= T1.ORG_CD ) AS orgDs, T1.TYPE_CD, T1.USE_YN, T1.ITEM_NM, T1.ITEM_DESC, T1.START_YEARMONTH, CASE WHEN T1.END_YEARMONTH IS NOT NULL AND LENGTH(T1.END_YEARMONTH)=6 THEN SUBSTR(T1.END_YEARMONTH,5) || '/' || SUBSTR(T1.END_YEARMONTH,0,4) ELSE T1.END_YEARMONTH END END_DT , T1.CRT_DT, T1.UPD_USER_ID, T2.CODE , T2.CODE_NAME , T2.CODE_DESC AS cdDs, to_char(T1.UPD_DT,'yyyy-mm-dd hh24:mi:ss') UPD_DT FROM CMM0042C T1 JOIN SYS0013M T2 ON T1.ITEM_CD = to_char(T2.CODE_ID)
AND #{searchDt} BETWEEN T1.START_YEARMONTH AND T1.END_YEARMONTH
JOIN CMM0041C T3 ON T1.ORG_SEQ=T3.ORG_SEQ
AND #{searchDt} BETWEEN T3.START_YEARMONTH AND T3.END_YEARMONTH
WHERE 1=1
AND T1.ORG_GR_CD = #{orgGrCombo}
AND T1.ORG_CD = #{orgCombo}
AND T1.USE_YN = #{useYnCombo}
ORDER BY T1.ITEM_NM


