mapper.select:
SELECT T1.ITEM_SEQ, T1.ITEM_CD, T1.ORG_SEQ, T1.ORG_GR_CD, T1.ORG_CD, (SELECT CODE_DESC FROM SYS0013M WHERE CODE_ID= T1.ORG_CD ) AS orgDs, T1.TYPE_CD, T1.ITEM_DESC, T2.CODE , T2.CODE_NAME , T2.CODE_DESC AS cdDs , T3.RULE_SEQ , T3.RULE_LEVEL , CASE WHEN T3.RULE_PID IS NULL THEN NULL WHEN T3.RULE_PID='0' THEN '' ELSE T3.RULE_PID END RULE_PID , T3.RULE_NM , T3.RULE_CATEGORY , T3.RULE_OPT1 , T3.RULE_OPT2 , T3.VALUE_TYPE , T3.VALUE_TYPE_NM , T3.RESULT_VALUE , T3.RESULT_VALUE_NM , T3.RULE_DESC , T3.START_YEARMONTH , T3.END_YEARMONTH , T3.USE_YN , CASE WHEN T3.START_YEARMONTH IS NOT NULL AND LENGTH(T3.START_YEARMONTH)=6 THEN SUBSTR(T3.START_YEARMONTH,5) || '/' || SUBSTR(T3.START_YEARMONTH,0,4) ELSE T3.START_YEARMONTH END START_DT , CASE WHEN T3.END_YEARMONTH IS NOT NULL AND LENGTH(T3.END_YEARMONTH)=6 THEN SUBSTR(T3.END_YEARMONTH,5) || '/' || SUBSTR(T3.END_YEARMONTH,0,4) ELSE T3.END_YEARMONTH END END_DT , T3.PRT_ORDER FROM CMM0042C T1 JOIN SYS0013M T2 ON T1.ITEM_CD = to_char(T2.CODE_ID) JOIN CMM0043M T3 ON T1.ITEM_SEQ = T3.ITEM_SEQ WHERE 1=1
AND T1.ORG_SEQ = #{orgSeq}
AND T1.ITEM_SEQ = #{itemSeq}
AND T1.ITEM_CD = #{itemCd}
AND T3.RULE_SEQ = #{ruleSeq}
START WITH RULE_PID=0
AND T3.USE_YN = #{useYnCombo}
AND #{searchDt} BETWEEN T3.START_YEARMONTH AND T3.END_YEARMONTH
CONNECT BY PRIOR RULE_SEQ =RULE_PID
AND T3.USE_YN = #{useYnCombo}
AND #{searchDt} BETWEEN T3.START_YEARMONTH AND T3.END_YEARMONTH
ORDER BY T3.RULE_LEVEL , T3.PRT_ORDER

SELECT SEQ , YEAR, MONTH, WEEKS, TO_CHAR(START_DT,'YYYY/MM/DD') START_DT, TO_CHAR(END_DT,'YYYY/MM/DD') END_DT, CRT_USER_ID, CRT_DT, UPD_USER_ID, UPD_DT FROM CMM0055M WHERE 1=1
AND YEAR=#{year} AND MONTH=#{month}
ORDER BY WEEKS

SELECT T1.ITEM_SEQ, T1.ITEM_CD, T1.ORG_SEQ, T1.ORG_GR_CD, T1.ORG_CD, (SELECT CODE_DESC FROM SYS0013M WHERE CODE_ID= T1.ORG_CD ) AS orgDs, T1.TYPE_CD, T1.USE_YN, T1.ITEM_NM, T1.ITEM_DESC, T1.START_YEARMONTH, CASE WHEN T1.END_YEARMONTH IS NOT NULL AND LENGTH(T1.END_YEARMONTH)=6 THEN SUBSTR(T1.END_YEARMONTH,5) || '/' || SUBSTR(T1.END_YEARMONTH,0,4) ELSE T1.END_YEARMONTH END END_DT , T1.CRT_DT, T1.UPD_USER_ID, T2.CODE , T2.CODE_NAME , T2.CODE_DESC AS cdDs, to_char(T1.UPD_DT,'dd-mm-yyyy hh24:mi:ss') UPD_DT FROM CMM0042T T1 JOIN SYS0013M T2 ON T1.ITEM_CD = to_char(T2.CODE_ID) AND T1.END_YEARMONTH = '999999' JOIN CMM0041C T3 ON T1.ORG_SEQ=T3.ORG_SEQ WHERE 1=1
AND T1.ORG_GR_CD = #{orgGrCombo}
AND T1.ITEM_SEQ = #{itemSeq}
AND T1.ORG_CD = #{orgCombo}
AND T1.USE_YN = #{useYnCombo}
ORDER BY T1.ITEM_NM

