mapper.select:
SELECT T2.CODE_MASTER_ID AS mstId , T1.CODE_MASTER_NAME AS mstNm, T2.CODE_ID AS cdId, T2.CODE AS cd, T2.CODE_NAME AS cdNm, T2.CODE_DESC AS cdDs FROM SYS0012M T1 JOIN SYS0013M T2 ON T1.CODE_MASTER_ID = T2.CODE_MASTER_ID WHERE 1=1
AND T1.CODE_MASTER_ID = #{mstId}
ORDER BY T2.CODE_ID

SELECT ORG_GR_CD, ORG_GR_NM, ORG_SEQ, ORG_CD, ORG_NM, USE_YN, CASE WHEN END_YEARMONTH IS NOT NULL AND LENGTH(END_YEARMONTH)=6 THEN SUBSTR(END_YEARMONTH,5) || '/' || SUBSTR(END_YEARMONTH,0,4) ELSE END_YEARMONTH END END_DT , T2.CODE_DESC AS cdDs FROM CMM0041C T1 JOIN SYS0013M T2 ON T1.ORG_CD = T2.CODE_ID WHERE 1=1
AND T1.ORG_GR_CD = #{orgRgCombo}
AND T1.ORG_CD = #{orgCombo}
AND T1.USE_YN = #{useYnCombo}
AND #{searchDt} BETWEEN START_YEARMONTH AND END_YEARMONTH
ORDER BY T1.ORG_GR_NM , T2.CODE_ID

mapper.update:
UPDATE CMM0041C
SET
END_YEARMONTH=(SELECT TO_CHAR(ADD_MONTHS(SYSDATE,-1),'YYYYMM') FROM DUAL)
, UPD_USER_ID=#{updUserId}
, UPD_DT=SYSDATE
WHERE ORG_GR_CD=#{orgGrCd} AND ORG_CD=#{orgCd} AND ORG_SEQ=#{orgSeq}

UPDATE CMM0041C
SET
USE_YN=#{useYn}
, UPD_USER_ID=#{updUserId}
, UPD_DT=SYSDATE
WHERE ORG_GR_CD=#{orgGrCd} AND ORG_CD=#{orgCd} AND ORG_SEQ=#{orgSeq}

mapper.select:
SELECT ORG_CD , ORG_NM FROM CMM0041C WHERE 1=1 AND START_YEARMONTH < END_YEARMONTH
AND ORG_GR_CD = #{orgRgCombo}
GROUP BY ORG_CD, ORG_NM ORDER BY ORG_CD

SELECT T1.ITEM_SEQ, T1.ITEM_CD, T1.ORG_SEQ, T1.ORG_GR_CD, T1.ORG_CD, (SELECT CODE_DESC FROM SYS0013M WHERE CODE_ID= T1.ORG_CD ) AS orgDs, T1.TYPE_CD, T1.USE_YN, T1.ITEM_DESC, T1.START_YEARMONTH, CASE WHEN T1.END_YEARMONTH IS NOT NULL AND LENGTH(T1.END_YEARMONTH)=6 THEN SUBSTR(T1.END_YEARMONTH,5) || '/' || SUBSTR(T1.END_YEARMONTH,0,4) ELSE T1.END_YEARMONTH END END_DT , T1.CRT_DT, T1.UPD_USER_ID, T1.UPD_DT , T2.CODE , T2.CODE_NAME , T2.CODE_DESC AS cdDs FROM CMM0042C T1 JOIN SYS0013M T2 ON T1.ITEM_CD = T2.CODE_ID
AND #{searchDt} BETWEEN T1.START_YEARMONTH AND T1.END_YEARMONTH
JOIN CMM0041C T3 ON T1.ORG_SEQ=T3.ORG_SEQ
AND #{searchDt} BETWEEN T3.START_YEARMONTH AND T3.END_YEARMONTH
WHERE 1=1
AND T1.ORG_GR_CD = #{orgRgCombo}
AND T1.ORG_CD = #{orgCombo}
AND T1.USE_YN = #{useYnCombo}


         SELECT
          ITEM_SEQ,
          ITEM_CD,
          ORG_SEQ
          FROM CMM0042C
        WHERE 1=1


			AND ORG_SEQ = #{orgSeq}


			AND ITEM_CD = #{itemCd}


			AND END_YEARMONTH=#{endDt}

mapper.update:
UPDATE CMM0042C
SET
END_YEARMONTH=(SELECT TO_CHAR(ADD_MONTHS(SYSDATE,-1),'YYYYMM') FROM DUAL)
, UPD_USER_ID=#{updUserId}
, UPD_DT=SYSDATE
WHERE ITEM_SEQ=#{itemSeq} AND ORG_SEQ=#{orgSeq} AND ITEM_CD=#{itemCd} AND END_YEARMONTH=#{endDt}

UPDATE CMM0042C
SET
USE_YN=#{useYn}

	,TYPE_CD = #{typeCd}

, UPD_USER_ID=#{updUserId}
, UPD_DT=SYSDATE
WHERE ITEM_SEQ=#{itemSeq} AND ORG_SEQ=#{orgSeq} AND ITEM_CD=#{itemCd}

UPDATE CMM0043M
SET
END_YEARMONTH=(SELECT TO_CHAR(ADD_MONTHS(SYSDATE,-1),'YYYYMM') FROM DUAL)
, UPD_USER_ID=#{updUserId}
, UPD_DT=SYSDATE
WHERE RULE_SEQ=#{ruleSeq} AND  ITEM_SEQ=#{itemSeq} AND ITEM_CD=#{itemCd} AND END_YEARMONTH=#{endDt}

mapper.select:
SELECT T1.ITEM_SEQ, T1.ITEM_CD, T1.ORG_SEQ, T1.ORG_GR_CD, T1.ORG_CD, (SELECT CODE_DESC FROM SYS0013M WHERE CODE_ID= T1.ORG_CD ) AS orgDs, T1.TYPE_CD, T1.ITEM_DESC, T2.CODE , T2.CODE_NAME , T2.CODE_DESC AS cdDs , T3.RULE_SEQ , T3.RULE_LEVEL , CASE WHEN T3.RULE_PID IS NULL THEN NULL WHEN T3.RULE_PID='0' THEN '' ELSE T3.RULE_PID END RULE_PID , T3.RULE_NM , T3.RULE_CATEGORY , T3.RULE_OPT1 , T3.RULE_OPT2 , T3.VALUE_TYPE , T3.VALUE_TYPE_NM , T3.RESULT_VALUE , T3.RESULT_VALUE_NM , T3.RULE_DESC , T3.START_YEARMONTH , T3.END_YEARMONTH , T3.USE_YN , CASE WHEN T3.START_YEARMONTH IS NOT NULL AND LENGTH(T3.START_YEARMONTH)=6 THEN SUBSTR(T3.START_YEARMONTH,5) || '/' || SUBSTR(T3.START_YEARMONTH,0,4) ELSE T3.START_YEARMONTH END START_DT , CASE WHEN T3.END_YEARMONTH IS NOT NULL AND LENGTH(T3.END_YEARMONTH)=6 THEN SUBSTR(T3.END_YEARMONTH,5) || '/' || SUBSTR(T3.END_YEARMONTH,0,4) ELSE T3.END_YEARMONTH END END_DT , T3.PRT_ORDER FROM CMM0042C T1 JOIN SYS0013M T2 ON T1.ITEM_CD = T2.CODE_ID JOIN CMM0043M T3 ON T1.ITEM_SEQ = T3.ITEM_SEQ WHERE 1=1
AND T1.ORG_SEQ = #{orgSeq}
AND T1.ITEM_SEQ = #{itemSeq}
AND T1.ITEM_CD = #{itemCd}
AND T3.RULE_SEQ = #{ruleSeq}
START WITH RULE_PID=0
AND T3.USE_YN = #{useYn}
AND #{searchDt} BETWEEN T3.START_YEARMONTH AND T3.END_YEARMONTH
CONNECT BY PRIOR RULE_SEQ =RULE_PID
AND T3.USE_YN = #{useYn}
AND #{searchDt} BETWEEN T3.START_YEARMONTH AND T3.END_YEARMONTH
ORDER BY T3.RULE_LEVEL , T3.PRT_ORDER
