mapper.select:
SELECT TOTAL_PRODUCT_COUNT
    FROM CP1005V EXTENT1
    WHERE EXTENT1.CUST_ID = #{cowayCustId}

SELECT
    CASE WHEN COUNT(*) > 0 THEN 'YES' ELSE 'NO' END IS_USER_HAS_ORD_NO
FROM SAL0001D
WHERE 1 = 1
AND CUST_ID = #{cowayCustId}
AND SALES_ORD_NO = #{orderNo}

