mapper.select:
SELECT CODE_ID , CODE_MASTER_ID, CODE , CODE_NAME, DBMS_LOB.SUBSTR (CODE_DESC, 4000) CODE_DESC, CASE WHEN (CRT_DT IS NOT NULL) THEN TO_CHAR(CRT_DT,'DD-MON-YYYY HH24:MI:SS') ELSE TO_CHAR(TO_DATE('19000101','YYYY-MM-DD'),'DD-MON-YYYY HH24:MI:SS') END AS CRT_DT, CASE WHEN (CRT_USER_ID IS NOT NULL) THEN CRT_USER_ID ELSE 0 END AS CRT_USER_ID, CASE WHEN (DISAB IS NOT NULL) THEN DISAB ELSE 0 END AS DISAB, CASE WHEN (UPD_DT IS NOT NULL) THEN TO_CHAR(UPD_DT,'DD-MON-YYYY HH24:MI:SS') ELSE TO_CHAR(TO_DATE('19000101','YYYY-MM-DD'),'DD-MON-YYYY HH24:MI:SS') END AS UPD_DT, CASE WHEN (UPD_USER_ID IS NOT NULL) THEN UPD_USER_ID ELSE 0 END AS UPD_USER_ID FROM SYS0013M WHERE CODE_MASTER_ID = #{mstCdId}
AND DISAB = #{mstDisabled}
AND DISAB = 0
ORDER BY CODE ASC
