 WHERE STUS_CTGRY_ID = #{stusCtgryId}
   AND STUS_CODE_ID  = #{stusCodeId}

mapper.insert:
 INSERT INTO SYS0038M
    (
      STUS_CODE_ID
     ,NAME
     ,CODE
   )
VALUES
   (
      ( SELECT MAX(STUS_CODE_ID) +1 FROM SYS0038M  )
    , #{codeName}
    , #{code}
  )

mapper.update:
UPDATE  SYS0038M
  SET NAME  = NVL(#{codeName},NAME)
    , CODE  = #{code}
WHERE STUS_CODE_ID = #{stusCodeId}

UPDATE  SYS0036M
  SET STUS_CTGRY_NAME  = NVL(#{stusCtgryName},STUS_CTGRY_NAME)
    , STUS_CTGRY_DESC  = NVL(#{stusCtgryDesc},DBMS_LOB.SUBSTR(STUS_CTGRY_NAME,4000) )
    , CRT_USER_ID      = #{crtUserId}
    , UPD_USER_ID      = #{updUserId}
    , UPD_DT           = SYSDATE
WHERE STUS_CTGRY_ID = #{stusCtgryId}

mapper.select:
SELECT A.STUS_CTGRY_ID ,A.STUS_CTGRY_NAME ,A.STUS_CTGRY_DESC ,A.CRT_USER_ID ,A.UPD_USER_ID ,A.UPD_DT FROM SYS0036M A WHERE 1=1
AND A.STUS_CTGRY_ID = #{txtCategoryId}
AND UPPER(A.STUS_CTGRY_NAME) LIKE '%' || UPPER(#{paramCategoryNM}) || '%'
AND A.CRT_USER_ID = #{paramCreateID}
ORDER BY A.STUS_CTGRY_ID , A.UPD_DT DESC

SELECT B.STUS_CODE_ID ,C.CODE ,C.NAME CODE_NAME ,B.SEQ_NO ,DECODE(B.DISAB, 0,'N',1,'Y','N') CODE_DISAB FROM SYS0036M A ,SYS0037M B ,SYS0038M C WHERE A.STUS_CTGRY_ID = B.STUS_CTGRY_ID AND B.STUS_CODE_ID = C.STUS_CODE_ID
AND A.STUS_CTGRY_ID = #{selCategoryId}
AND B.DISAB = #{parmDisab}
ORDER BY B.SEQ_NO

SELECT CODE_ID , CODE , CODE_NAME , DBMS_LOB.SUBSTR (CODE_DESC, 4000) DESCRIPTION FROM SYS0013M WHERE DISAB = 0
AND CODE_MASTER_ID = #{groupCode}
AND CODE = #{Codeval}
AND CODE LIKE #{likeValue}||'%'
AND CODE NOT LIKE #{notlike}||'%'
AND CODE IN
#{item}
AND CODE IN (SELECT REGEXP_SUBSTR (#{codeIn}, '[^,]+', 1, LEVEL) FROM DUAL CONNECT BY REGEXP_SUBSTR (#{codeIn}, '[^,]+', 1, LEVEL) IS NOT NULL)
ORDER BY ${orderValue}
ORDER BY CODE_NAME

SELECT CODE_NAME
FROM SYS0013M
WHERE CODE_ID = #{value}

SELECT CODE_ID detailCodeId, CODE_MASTER_ID, CODE detailCode, CODE_NAME detailCodeName, DBMS_LOB.SUBSTR (CODE_DESC, 4000) detailCodeDesc, CASE WHEN (CRT_DT IS NOT NULL) THEN TO_CHAR(CRT_DT,'DD-MON-YYYY HH24:MI:SS') ELSE TO_CHAR(TO_DATE('19000101','YYYY-MM-DD'),'DD-MON-YYYY HH24:MI:SS') END AS CRT_DT, CASE WHEN (CRT_USER_ID IS NOT NULL) THEN CRT_USER_ID ELSE 0 END AS CRT_USER_ID, DECODE(CASE WHEN (DISAB IS NOT NULL) THEN DISAB ELSE 0 END , 0,'N',1,'Y','N') AS detailDisabled, CASE WHEN (UPD_DT IS NOT NULL) THEN TO_CHAR(UPD_DT,'DD-MON-YYYY HH24:MI:SS') ELSE TO_CHAR(TO_DATE('19000101','YYYY-MM-DD'),'DD-MON-YYYY HH24:MI:SS') END AS UPD_DT, CASE WHEN (UPD_USER_ID IS NOT NULL) THEN UPD_USER_ID ELSE 0 END AS UPD_USER_ID FROM SYS0013M WHERE CODE_MASTER_ID = #{mstCdId}
AND DISAB = #{dtailDisabled}
ORDER BY CODE ASC

mapper.update:
UPDATE SYS0012M
   SET CODE_DESC   = NVL(#{codeDesc},DBMS_LOB.SUBSTR (#{codeMasterName},4000) )
     , CODE_MASTER_NAME = #{codeMasterName}
     , DISAB       = DECODE(#{disabled},'N',0,'Y',1,0)
     , UPD_USER_ID = #{updUserId}
     , UPD_DT      = SYSDATE
WHERE CODE_MASTER_ID = #{codeMasterId}

UPDATE SYS0013M
   SET CODE        = #{detailcode}
     , CODE_NAME   = #{detailcodename}
     , CODE_DESC   = #{detailcodedesc}
     , DISAB       = DECODE(#{detaildisabled},'N',0,'Y',1,0)
     , UPD_USER_ID = #{updUserId}
     , UPD_DT      = SYSDATE
WHERE CODE_ID      = #{detailcodeid}

mapper.select:
SELECT


              country CODE_ID , country CODE_NAME


              state CODE_ID , state CODE_NAME


              area CODE_ID , area CODE_NAME


              POSTCODE CODE_ID , POSTCODE CODE_NAME


  FROM sys0064m
  WHERE STATUS_ID = '1'

        group by country


        AND COUNTRY = #{country}
        group by state



       AND COUNTRY = #{country}
       AND STATE   = #{state}
        group by area


       AND COUNTRY = #{country}
       AND STATE   = #{state}
       AND AREA    = #{area}
        group by POSTCODE

SELECT WH_LOC_ID CODE_ID, WH_LOC_CODE CODE , DBMS_LOB.SUBSTR (WH_LOC_DESC, 4000) CODE_NAME
  FROM SYS0028M S28M
 WHERE 1 = 1
   AND S28M.WH_LOC_STUS_ID = 1



            AND S28M.WH_LOC_STK_GRAD <> #{grade}


             AND S28M.WH_LOC_STK_GRAD = #{grade}




      AND S28M.WH_LOC_CODE = #{sLoc}


      AND S28M.WH_LOC_BRNCH_ID = #{brnch}


      AND S28M.WH_LOC_GB IN

       #{item}





              AND S28M.WH_LOC_GB in ('01','05')


              AND S28M.WH_LOC_GB in ('01','02','05')


              AND S28M.WH_LOC_GB in ('03','04')


              AND S28M.WH_LOC_GB = #{locgb}




      AND S28M.CDC_CODE = (SELECT WH_LOC_CODE FROM SYS0028M WHERE WH_LOC_ID =#{cdcloc})


      AND S28M.RDC_CODE = (SELECT WH_LOC_CODE FROM SYS0028M WHERE WH_LOC_ID =#{rdcloc})


      AND S28M.RDC_CODE = (SELECT RDC_CODE FROM SYS0028M WHERE WH_LOC_ID =#{ctloc})


      AND S28M.WH_LOC_BRNCH_ID IN (SELECT BRNCH_ID FROM SYS0005M WHERE (CASE WHEN (TYPE_ID IS NOT NULL) THEN TYPE_ID ELSE 0 END) = 42)

 ORDER BY WH_LOC_GB , CODE_NAME

SELECT CODE
FROM SYS0004M
WHERE BANK_ID = #{value}
