SELECT A.STUS_CTGRY_ID ,A.STUS_CTGRY_NAME ,A.STUS_CTGRY_DESC ,A.CRT_USER_ID ,A.UPD_USER_ID ,A.UPD_DT FROM SYS0036M A WHERE 1=1
AND A.STUS_CTGRY_ID = #{txtCategoryId}
AND UPPER(A.STUS_CTGRY_NAME) LIKE '%' || UPPER(#{paramCategoryNM}) || '%'
AND A.CRT_USER_ID = #{paramCreateID}
ORDER BY A.STUS_CTGRY_ID , A.UPD_DT DESC

SELECT B.STUS_CODE_ID ,C.CODE ,C.NAME CODE_NAME ,B.SEQ_NO ,DECODE(B.DISAB, 0,'N',1,'Y','N') CODE_DISAB FROM SYS0036M A ,SYS0037M B ,SYS0038M C WHERE A.STUS_CTGRY_ID = B.STUS_CTGRY_ID AND B.STUS_CODE_ID = C.STUS_CODE_ID
AND A.STUS_CTGRY_ID = #{selCategoryId}
AND B.DISAB = #{parmDisab}
ORDER BY B.SEQ_NO

