and CODE_MASTER_ID = (select code_master_id from sys0012m where CODE_MASTER_NAME = #{codeMasterNameVal})
AND CODE NOT LIKE #{notlike}||'%'
AND CODE IN
#{item}
AND CODE IN (SELECT REGEXP_SUBSTR (#{codeIn}, '[^,]+', 1, LEVEL) FROM DUAL CONNECT BY REGEXP_SUBSTR (#{codeIn}, '[^,]+', 1, LEVEL) IS NOT NULL)
AND CODE_NAME = #{codeName}
UNION ALL
SELECT CODE_ID , CODE , CODE_NAME , DBMS_LOB.SUBSTR (CODE_DESC, 4000) DESCRIPTION FROM SYS0013M WHERE DISAB = 0 AND CODE_MASTER_ID = 556
ORDER BY ${orderValue}
ORDER BY CODE_NAME

