SELECT STK_ID , C1 , CODE_NAME FROM ( SELECT DISTINCT T1.STK_ID , T1.STK_CODE ||' - '||T1.STK_DESC C1 , T3.CODE_NAME FROM SYS0026M T1 , SYS0013M T3 WHERE T1.STK_CTGRY_ID = T3.CODE_ID AND 61 = T1.STK_TYPE_ID AND T1.STUS_CODE_ID = 1
/* Exclusion Homecare */ AND NOT EXISTS (SELECT M1.STK_CTGRY_ID FROM SYS0026M M1 WHERE M1.STK_CTGRY_ID IN (SELECT AA.CODE_ID FROM SYS0013M AA, SYS0094M BB WHERE AA.CODE = BB.CODE AND AA.CODE_MASTER_ID = 11 AND BB.IND = 'HOMECARE') /* STKCATEGORYID_HOMECARE */ AND M1.STK_CTGRY_ID =T1.STK_CTGRY_ID )
/* Only Homecare */ AND EXISTS (SELECT M1.STK_CTGRY_ID FROM SYS0026M M1 WHERE M1.STK_CTGRY_ID IN (SELECT AA.CODE_ID FROM SYS0013M AA, SYS0094M BB WHERE AA.CODE = BB.CODE AND AA.CODE_MASTER_ID = 11 AND BB.IND = 'HOMECARE') /* STKCATEGORYID_HOMECARE */ AND M1.STK_CTGRY_ID =T1.STK_CTGRY_ID )
AND T1.STK_CTGRY_ID in (SELECT M1.STK_CTGRY_ID FROM SYS0026M M1 WHERE M1.STK_CTGRY_ID IN (SELECT AA.CODE_ID FROM SYS0013M AA, SYS0094M BB WHERE AA.CODE = BB.CODE AND BB.DISB = 0 AND AA.CODE_MASTER_ID = 11 AND BB.IND = 'LOAN_PROD'))
) ORDER BY CODE_NAME ASC, STK_ID ASC

