mapper.select:
SELECT CODE_ID , CODE , CODE_NAME , DBMS_LOB.SUBSTR (CODE_DESC, 4000) DESCRIPTION FROM SYS0013M WHERE DISAB = 0 AND CODE_MASTER_ID = #{groupCode}
ORDER BY #{orderValue}
ORDER BY CODE_NAME

SELECT ACC_ID AS ACC_ID ,ACC_CODE AS ACC_CODE ,ACC_DESC AS ACC_DESC ,ACC_CRT_USER_ID AS ACC_CRT_USER_ID ,ACC_UPD_USER_ID AS ACC_UPD_USER_ID ,ACC_STUS_ID AS ACC_STUS_ID ,IS_PAY_CASH AS IS_PAY_CASH ,IS_PAY_ONLINE AS IS_PAY_ONLINE ,IS_PAY_CHQ AS IS_PAY_CHQ ,IS_PAY_CRC AS IS_PAY_CRC ,SYS0038M.Code AS STATUS_CODE ,SYS0038M.NAME AS STATUS_NAME ,CASE WHEN (ACC_CURR_ID IS NOT NULL) THEN ACC_CURR_ID ELSE '' END AS ACC_CURR_ID ,CASE WHEN (ACC_GRP IS NOT NULL) THEN ACC_GRP ELSE '' END AS ACC_GRP ,CASE WHEN (ACC_IS_MFG IS NOT NULL) THEN ACC_IS_MFG ELSE 0 END AS ACC_IS_MFG ,CASE WHEN (ACC_LINK IS NOT NULL) THEN ACC_LINK ELSE '' END AS ACC_LINK ,CASE WHEN (ACC_PAY_TYPE_ID IS NOT NULL) THEN ACC_PAY_TYPE_ID ELSE 0 END AS ACC_PAY_TYPE_ID ,CASE WHEN (ACC_TYPE IS NOT NULL) THEN ACC_TYPE ELSE 0 END AS ACC_TYPE ,CASE WHEN (ACC_ADDR1 IS NOT NULL) THEN DBMS_LOB.SUBSTR (ACC_ADDR1,4000) ELSE '' END AS ACC_ADDR1 ,CASE WHEN (ACC_ADDR2 IS NOT NULL) THEN DBMS_LOB.SUBSTR (ACC_ADDR2,4000) ELSE '' END AS ACC_ADDR2 ,CASE WHEN (ACC_ADDR3 IS NOT NULL) THEN DBMS_LOB.SUBSTR (ACC_ADDR3,4000) ELSE '' END AS ACC_ADDR3 ,CASE WHEN (ACC_ADD_AREA_ID IS NOT NULL) THEN ACC_ADD_AREA_ID ELSE 0 END AS ACC_ADD_AREA_ID ,CASE WHEN (Extent4.AREA_ID IS NOT NULL) THEN Extent4.AREA_NAME ELSE '' END AS AREA_NAME ,CASE WHEN (ACC_ADD_CNTY_ID IS NOT NULL) THEN ACC_ADD_CNTY_ID ELSE 0 END AS ACC_ADD_CNTY_ID ,CASE WHEN (Extent6.CNTY_ID IS NOT NULL) THEN Extent6.NAME ELSE '' END AS NAME15 ,CASE WHEN (ACC_CRT_DT IS NOT NULL) THEN TO_CHAR(ACC_CRT_DT,'DD-MON-YYYY HH24:MI:SS') ELSE TO_CHAR(TO_DATE('19000101','YYYY-MM-DD'),'DD-MON-YYYY HH24:MI:SS') END AS ACC_CRT_DT ,CASE WHEN (Extent7.USER_ID IS NOT NULL) THEN Extent7.USER_NAME ELSE '' END AS USER_NAME47 ,CASE WHEN (ACC_ADD_POST_CODE_ID IS NOT NULL) THEN ACC_ADD_POST_CODE_ID ELSE 0 END AS ACC_ADD_POST_CODE_ID ,CASE WHEN (Extent3.POST_CODE_ID IS NOT NULL) THEN Extent3.POST_CODE_ID ELSE 0 END AS POST_CODE_ID ,CASE WHEN (SAP_ACC_CODE IS NOT NULL) THEN SAP_ACC_CODE ELSE '' END AS SAP_ACC_CODE ,CASE WHEN (ACC_ADD_STATE_ID IS NOT NULL) THEN ACC_ADD_STATE_ID ELSE 0 END AS ACC_ADD_STATE_ID ,CASE WHEN (Extent5.STATE_ID IS NOT NULL) THEN Extent5.NAME ELSE '' END AS NAME35 ,CASE WHEN (ACC_TEL_NO1 IS NOT NULL) THEN ACC_TEL_NO1 ELSE '' END AS ACC_TEL_NO1 ,CASE WHEN (ACC_TEL_NO2 IS NOT NULL) THEN ACC_TEL_NO2 ELSE '' END AS ACC_TEL_NO2 ,CASE WHEN (ACC_UPD_DT IS NOT NULL) THEN TO_CHAR(ACC_UPD_DT,'DD-MON-YYYY HH24:MI:SS') ELSE TO_CHAR(TO_DATE('19000101','YYYY-MM-DD'),'DD-MON-YYYY HH24:MI:SS') END AS ACC_UPD_DT ,CASE WHEN (Extent8.USER_ID IS NOT NULL) THEN Extent8.USER_NAME ELSE '' END AS USER_NAME FROM SYS0001M INNER JOIN SYS0038M ON SYS0038M.STUS_CODE_ID = ACC_STUS_ID LEFT OUTER JOIN SYS0030C Extent3 ON Extent3.POST_CODE_ID = ACC_ADD_POST_CODE_ID LEFT OUTER JOIN SYS0039M Extent4 ON Extent4.AREA_ID = ACC_ADD_AREA_ID LEFT OUTER JOIN SYS0035M Extent5 ON Extent5.STATE_ID = ACC_ADD_STATE_ID LEFT OUTER JOIN SYS0015M Extent6 ON Extent6.CNTY_ID = ACC_ADD_CNTY_ID LEFT OUTER JOIN SYS0047M Extent7 ON Extent7.USER_ID = ACC_CRT_USER_ID LEFT OUTER JOIN SYS0047M Extent8 ON Extent8.USER_ID = ACC_UPD_USER_ID WHERE 1 = 1
AND ACC_STUS_ID = #{accStusId}
AND ACC_ID = #{accId}
AND ACC_CODE = #{accCode}
AND ACC_DESC LIKE '%' || #{accDesc} || '%'
AND SAP_ACC_CODE = #{sapAccCode}
AND IS_PAY_CASH = 1
AND IS_PAY_ONLINE = 1
AND IS_PAY_CHQ = 1
AND IS_PAY_CRC = 1
ORDER BY ACC_CODE ASC

SELECT CODE_ID detailCodeId, CODE_MASTER_ID, CODE detailCode, CODE_NAME detailCodeName, DBMS_LOB.SUBSTR (CODE_DESC, 4000) detailCodeDesc, CASE WHEN (CRT_DT IS NOT NULL) THEN TO_CHAR(CRT_DT,'DD-MON-YYYY HH24:MI:SS') ELSE TO_CHAR(TO_DATE('19000101','YYYY-MM-DD'),'DD-MON-YYYY HH24:MI:SS') END AS CRT_DT, CASE WHEN (CRT_USER_ID IS NOT NULL) THEN CRT_USER_ID ELSE 0 END AS CRT_USER_ID, DECODE(CASE WHEN (DISAB IS NOT NULL) THEN DISAB ELSE 0 END , 0,'N',1,'Y','N') AS detailDisabled, CASE WHEN (UPD_DT IS NOT NULL) THEN TO_CHAR(UPD_DT,'DD-MON-YYYY HH24:MI:SS') ELSE TO_CHAR(TO_DATE('19000101','YYYY-MM-DD'),'DD-MON-YYYY HH24:MI:SS') END AS UPD_DT, CASE WHEN (UPD_USER_ID IS NOT NULL) THEN UPD_USER_ID ELSE 0 END AS UPD_USER_ID FROM SYS0013M WHERE CODE_MASTER_ID = #{mstCdId}
AND DISAB = #{dtailDisabled}
ORDER BY CODE ASC

mapper.update:
UPDATE SYS0012M
   SET CODE_DESC   = NVL(#{codeDesc},DBMS_LOB.SUBSTR (#{codeMasterName},4000) ) 
     , CODE_MASTER_NAME = #{codeMasterName}
     , DISAB       = DECODE(#{disabled},'N',0,'Y',1,0)
     , UPD_USER_ID = #{updUserId}
     , UPD_DT      = SYSDATE  
WHERE CODE_MASTER_ID = #{codeMasterId}

UPDATE SYS0013M
   SET CODE        = #{detailcode}
     , CODE_NAME   = #{detailcodename}
     , CODE_DESC   = DBMS_LOB.SUBSTR (#{detailcodedesc}, 4000)  
     , DISAB       = DECODE(#{detaildisabled},'N',0,'Y',1,0)
     , UPD_USER_ID = #{crtUserId}
     , UPD_DT      = SYSDATE  
WHERE CODE_ID      = #{detailcodeid}
