mapper.select:
SELECT ZRE_EXPT_ID , SAL30D.DEALER_NAME , SYS38M.NAME CODE_NAME , ZRE_EXPT_DEALER_ID , ZRE_EXPT_STUS_ID , ZRE_EXPT_REM , ZRE_EXPT_CRT_USER_ID , ZRE_EXPT_CRT_DT FROM SYS0067M SYS67M , SAL0030D SAL30D , SYS0038M SYS38M WHERE SAL30D.DEALER_ID(+) = SYS67M.ZRE_EXPT_DEALER_ID AND SYS38M.STUS_CODE_ID(+) = SYS67M.ZRE_EXPT_STUS_ID
AND SYS67M.ZRE_EXPT_ID = #{zreExptId}
AND UPPER(DEALER_NAME) LIKE '%' || UPPER(#{dealerName}) || '%'
AND SYS67M.ZRE_EXPT_STUS_ID = #{zreExptStusId}
ORDER BY ZRE_EXPT_ID ASC
