AND A.RGST_DT = #{searchDate} GROUP BY A.STATUS ) X WHERE Z.STATUS = X.STATUS(+)

/* Unbilled Revenue */ SELECT Z.INSTALL_DT AS BIZ_COL , SND_CNT AS SND_CNT , RCV_CNT AS RCV_CNT , Z.UNBILL AS SND_CHK_VAL , X.UNBILL AS RCV_CHK_VAL , Z.INSTALL_DT||ROWNUM AS ROW_ID FROM ( SELECT INSTALL_DT , COUNT(1) AS SND_CNT , SUM(UNBILL) AS UNBILL FROM ( SELECT TO_CHAR(IR.INSTALL_DT,'YYYYMM') AS INSTALL_DT , ROUND(ROUND((TO_NUMBER(TO_CHAR(LAST_DAY(IR.INSTALL_DT),'DD')) - TO_NUMBER(TO_CHAR(IR.INSTALL_DT,'DD')) + 1),2) / ROUND(TO_NUMBER(TO_CHAR(LAST_DAY(IR.INSTALL_DT),'DD')),2) * (CASE WHEN EC.EURC_ID IS NOT NULL THEN M.MTH_RENT_AMT ELSE ( CASE WHEN RR.RLIEF_ID IS NOT NULL THEN M.MTH_RENT_AMT ELSE ROUND(M.MTH_RENT_AMT,2) * 100 / 106 END ) END ) ,2) AS UNBILL FROM SAL0001D M JOIN SAL0002D D ON D.SALES_ORD_ID = M.SALES_ORD_ID JOIN SYS0026M STK ON STk.STK_ID = D.ITM_STK_ID JOIN SYS0013M CD ON CODE_ID = STK.STK_CTGRY_ID JOIN SAL0046D T ON T.INSTALL_ENTRY_ID = (SELECT MIN(INSTALL_ENTRY_ID) FROM SAL0046D F WHERE F.SALES_ORD_ID = M.SALES_ORD_ID AND F.STUS_CODE_ID = 4) JOIN SAL0047D IR ON IR.RESULT_ID = (SELECT MAX(TT.RESULT_ID) FROM SAL0047D TT WHERE TT.ENTRY_ID = T.INSTALL_ENTRY_ID) JOIN SAL0071D SS ON SS.SALES_ORD_ID = M.SALES_ORD_ID LEFT JOIN SAL0042D EC ON EC.EURC_SALES_ORD_ID = M.SALES_ORD_ID LEFT JOIN SAL0043D RR ON RR.RLIEF_ORD_ID = M.SALES_ORD_ID /**/ LEFT JOIN PAY0016D PD ON PD.ACC_BILL_ORD_ID = M.SALES_ORD_ID AND TO_CHAR(PD.ACC_BILL_REF_DT,'YYYYMM') = #{searchDate} LEFT JOIN SAL0002D H ON M.SALES_ORD_ID = H.SALES_ORD_ID LEFT JOIN ORG0001D G ON M.MEM_ID = G.MEM_ID LEFT JOIN SYS0005M I ON G.BRNCH = I.BRNCH_ID LEFT JOIN SYS0026M J ON H.ITM_STK_ID = J.STK_ID LEFT JOIN SYS0046D K ON K.CODE_ID = PD.ACC_BILL_TAX_CODE_ID /**/ WHERE M.APP_TYPE_ID = 66 AND M.SALES_ORD_NO like '2%' AND T.STUS_CODE_ID = 4 AND SS.STUS_CODE_ID NOT IN ('TER','RET','WOF') AND TO_CHAR(IR.INSTALL_DT,'YYYYMM') = #{searchDate} GROUP BY M.SALES_ORD_NO , IR.INSTALL_DT , SS.STUS_CODE_ID , EC.EURC_ID , M.MTH_RENT_AMT , RR.RLIEF_ID , CD.CODE_NAME , M.APP_TYPE_ID , I.CODE , G.MEM_CODE , M.ORG_CODE , M.DEPT_CODE , G.MEM_TYPE , J.STK_CTGRY_ID , K.CODE , J.STK_CODE , M.CUST_ID , I.REGN_ID , M.BRNCH_ID , G.BRNCH ) GROUP BY INSTALL_DT ) Z , ( /* Interface Table */ SELECT SUBSTR(A.INST_DATE,0,6) AS INSTALL_DT , MAX(A.IF_TYPE) AS IF_TYPE , MAX(B.IF_TYPE_NM) AS IF_TYPE_NM , COUNT(*) AS RCV_CNT , SUM(A.UNBILL) AS UNBILL FROM ITF0963M A , ITF0001M B WHERE A.IF_TYPE = B.IF_TYPE (+)
AND A.TRAN_STATUS_CD = #{tranStatusCd}
AND SUBSTR(A.INST_DATE,0,6) = #{searchDate} GROUP BY SUBSTR(A.INST_DATE,0,6) ) X WHERE Z.INSTALL_DT = X.INSTALL_DT(+)

/* Bank Charge */ SELECT X.BANKAC AS BIZ_COL , NULL AS SND_CNT , X.RCV_CNT AS RCV_CNT , NULL AS SND_CHK_VAL , X.NET_AMT AS RCV_CHK_VAL , X.BANKAC||ROWNUM AS ROW_ID FROM ( /* Interface Table of Billing (CN_DN) */ SELECT A.BANKAC AS BANKAC , MAX(A.IF_TYPE) AS IF_TYPE , MAX(B.IF_TYPE_NM) AS IF_TYPE_NM , SUM(A.ADV_AMT+A.DIFF_AMT) AS NET_AMT, COUNT(1) AS RCV_CNT FROM ITF0942M A , ITF0001M B WHERE A.IF_TYPE = B.IF_TYPE(+)
AND A.TRAN_STATUS_CD = #{tranStatusCd}
AND SUBSTR(A.PAY_DT,0,6) = #{searchDate} GROUP BY A.BANKAC ) X

/* Monitoring Collection */ SELECT * FROM ( SELECT B.IF_TYPE , C.IF_TYPE_NM , B.TRAN_STATUS_CD , B.RGST_DT , C.BIZ_COL , A.WOR_NO AS SND_CHK_COL , B.WOR_NO AS RCV_CHK_COL , C.CHK_COL , A.CUST_AMT AS SND_CHK_VAL , B.CUST_AMT AS RCV_CHK_VAL ,
CASE WHEN A.CUST_AMT <> B.CUST_AMT THEN 0 ELSE ROWNUM END AS ORDER_SEQ ,
B.IF_TYPE||B.WOR_NO||ROWNUM as ROW_ID FROM ( SELECT A.WOR_NO AS WOR_NO , SUM(A.CUST_AMT) AS CUST_AMT FROM ( SELECT A.OR_NO AS WOR_NO , H.PAY_ITM_AMT AS CUST_AMT FROM PAY0064D A , PAY0065D B , PAY0252T H /* PAYMENT GROUP _ ADD ORDER*/ WHERE A.PAY_ID = B.PAY_ID AND A.PAY_ID = H.PAY_ID AND B.PAY_ITM_ID = H.PAY_ITM_ID AND TO_CHAR(A.CRT_DT,'YYYYMM') = #{searchDate} UNION ALL SELECT A.OR_NO AS WOR_NO , A.TOT_AMT AS CUST_AMT FROM PAY0064D A , PAY0065D B WHERE A.PAY_ID = B.PAY_ID AND SUBSTR(A.OR_NO,0,3) = 'EOR' AND TO_CHAR(A.CRT_DT,'YYYYMM') = #{searchDate} UNION ALL SELECT A.OR_NO AS WOR_NO , A.TOT_AMT AS CUST_AMT FROM PAY0064D A , PAY0065D B , PAY0064D H WHERE A.PAY_ID = B.PAY_ID AND A.MTCH_ID = H.PAY_ID AND SUBSTR(A.OR_NO,0,3) = 'ROR' AND TO_CHAR(A.CRT_DT,'YYYYMM') = #{searchDate} ) A WHERE SUBSTR(A.WOR_NO,0,3) = #{searchParam} GROUP BY A.WOR_NO ) A , ( /* Interface Table */ SELECT A.WOR_NO AS WOR_NO , MAX(A.IF_TYPE) AS IF_TYPE , MAX(B.IF_TYPE_NM) AS IF_TYPE_NM , A.TRAN_STATUS_CD AS TRAN_STATUS_CD , A.RGST_DT , SUM(A.CUST_AMT) AS CUST_AMT FROM ITF0910M A , ITF0001M B WHERE A.IF_TYPE = B.IF_TYPE (+)
AND A.TRAN_STATUS_CD = #{tranStatusCd}
AND SUBSTR(PAY_DATE,0,6) = #{searchDate} AND SUBSTR(A.WOR_NO,0,3) = #{searchParam} GROUP BY A.WOR_NO,A.TRAN_STATUS_CD,A.RGST_DT ) B , ITF0001M C WHERE A.WOR_NO = B.WOR_NO(+) AND B.IF_TYPE = C.IF_TYPE(+) ) ORDER BY ORDER_SEQ

/* Interface Customer Add */ SELECT * FROM ( SELECT B.IF_TYPE , C.IF_TYPE_NM , B.TRAN_STATUS_CD , B.RGST_DT , C.BIZ_COL , NULL AS SND_CHK_COL, B.CUST_NO AS RCV_CHK_COL, C.CHK_COL , NULL AS SND_CHK_VAL , NULL AS RCV_CHK_VAL , ROWNUM AS ORDER_SEQ , B.IF_TYPE||B.CUST_NO||ROWNUM as ROW_ID FROM ITF0110M B , ITF0001M C WHERE B.IF_TYPE = C.IF_TYPE(+)
AND B.TRAN_STATUS_CD = #{tranStatusCd}
AND SUBSTR(B.RGST_DT,0,6) = #{searchDate} ORDER BY B.CUST_NO ) ORDER BY ORDER_SEQ

/* Installation Result. */ SELECT * FROM ( SELECT B.IF_TYPE , B.IF_TYPE_NM , B.TRAN_STATUS_CD , B.RGST_DT , B.BIZ_COL , A.SALES_ORD_NO||'_'||A.APP_TYPE_ID AS SND_CHK_COL , B.SALES_ORD_NO||'_'||B.APP_TYPE_ID AS RCV_CHK_COL , B.CHK_COL , NULL AS SND_CHK_VAL , NULL AS RCV_CHK_VAL ,
CASE WHEN A.APP_TYPE_ID <> B.APP_TYPE_ID THEN 0 ELSE ROWNUM END AS ORDER_SEQ ,
B.IF_TYPE||B.APP_TYPE_ID||ROWNUM as ROW_ID FROM ( SELECT A.APP_TYPE_ID , A.SALES_ORD_NO FROM SAL0001D A , SAL0046D B , SAL0047D C , SAL0002D D , SYS0026M E , SYS0005M F WHERE A.SALES_ORD_ID = B.SALES_ORD_ID AND B.INSTALL_ENTRY_ID = C.ENTRY_ID AND B.STUS_CODE_ID = 4 AND C.STUS_CODE_ID = 4 AND A.SALES_ORD_ID = D.SALES_ORD_ID AND D.ITM_STK_ID = E.STK_ID AND A.BRNCH_ID = F.BRNCH_ID AND TO_CHAR(C.INSTALL_DT,'YYYYMM') = #{searchDate} ) A , ( SELECT A.APP_TYPE AS APP_TYPE_ID , A.ORDER_NO AS SALES_ORD_NO, A.IF_TYPE AS IF_TYPE , A.TRAN_STATUS_CD , A.RGST_DT , B.IF_TYPE_NM AS IF_TYPE_NM , B.BIZ_COL AS BIZ_COL , B.CHK_COL AS CHK_COL FROM ITF0210M A , ITF0001M B WHERE A.IF_TYPE = B.IF_TYPE(+)
AND A.TRAN_STATUS_CD = #{tranStatusCd}
AND SUBSTR(A.INSTALL_DATE,0,6) = #{searchDate} ) B WHERE A.APP_TYPE_ID = B.APP_TYPE_ID(+) AND A.SALES_ORD_NO = B.SALES_ORD_NO(+) ) ORDER BY ORDER_SEQ

/* Unbilled Revenue */ SELECT * FROM ( SELECT B.IF_TYPE , B.IF_TYPE_NM , B.TRAN_STATUS_CD , B.RGST_DT , B.BIZ_COL , A.INSTALL_DT AS SND_CHK_COL , B.INSTALL_DT AS RCV_CHK_COL , B.CHK_COL , A.UNBILL AS SND_CHK_VAL, B.UNBILL AS RCV_CHK_VAL ,
CASE WHEN A.UNBILL <> B.UNBILL THEN 0 ELSE RANK() OVER(ORDER BY A.INSTALL_DT) END AS ORDER_SEQ ,
B.IF_TYPE||B.INSTALL_DT||ROWNUM as ROW_ID FROM ( SELECT INSTALL_DT , COUNT(1) AS SND_CNT , SUM(UNBILL) AS UNBILL FROM ( SELECT TO_CHAR(IR.INSTALL_DT,'YYYYMMDD') AS INSTALL_DT , ROUND(ROUND((TO_NUMBER(TO_CHAR(LAST_DAY(IR.INSTALL_DT),'DD')) - TO_NUMBER(TO_CHAR(IR.INSTALL_DT,'DD')) + 1),2) / ROUND(TO_NUMBER(TO_CHAR(LAST_DAY(IR.INSTALL_DT),'DD')),2) * (CASE WHEN EC.EURC_ID IS NOT NULL THEN M.MTH_RENT_AMT ELSE ( CASE WHEN RR.RLIEF_ID IS NOT NULL THEN M.MTH_RENT_AMT ELSE ROUND(M.MTH_RENT_AMT,2) * 100 / 106 END ) END ) ,2) AS UNBILL FROM SAL0001D M JOIN SAL0002D D ON D.SALES_ORD_ID = M.SALES_ORD_ID JOIN SYS0026M STK ON STk.STK_ID = D.ITM_STK_ID JOIN SYS0013M CD ON CODE_ID = STK.STK_CTGRY_ID JOIN SAL0046D T ON T.INSTALL_ENTRY_ID = (SELECT MIN(INSTALL_ENTRY_ID) FROM SAL0046D F WHERE F.SALES_ORD_ID = M.SALES_ORD_ID AND F.STUS_CODE_ID = 4) JOIN SAL0047D IR ON IR.RESULT_ID = (SELECT MAX(TT.RESULT_ID) FROM SAL0047D TT WHERE TT.ENTRY_ID = T.INSTALL_ENTRY_ID) JOIN SAL0071D SS ON SS.SALES_ORD_ID = M.SALES_ORD_ID LEFT JOIN SAL0042D EC ON EC.EURC_SALES_ORD_ID = M.SALES_ORD_ID LEFT JOIN SAL0043D RR ON RR.RLIEF_ORD_ID = M.SALES_ORD_ID /**/ LEFT JOIN PAY0016D PD ON PD.ACC_BILL_ORD_ID = M.SALES_ORD_ID AND TO_CHAR(PD.ACC_BILL_REF_DT,'YYYYMM') = #{searchDate} LEFT JOIN SAL0002D H ON M.SALES_ORD_ID = H.SALES_ORD_ID LEFT JOIN ORG0001D G ON M.MEM_ID = G.MEM_ID LEFT JOIN SYS0005M I ON G.BRNCH = I.BRNCH_ID LEFT JOIN SYS0026M J ON H.ITM_STK_ID = J.STK_ID LEFT JOIN SYS0046D K ON K.CODE_ID = PD.ACC_BILL_TAX_CODE_ID /**/ WHERE M.APP_TYPE_ID = 66 AND M.SALES_ORD_NO like '2%' AND T.STUS_CODE_ID = 4 AND SS.STUS_CODE_ID NOT IN ('TER','RET','WOF') AND TO_CHAR(IR.INSTALL_DT,'YYYYMM') = #{searchDate} GROUP BY M.SALES_ORD_NO , IR.INSTALL_DT , SS.STUS_CODE_ID , EC.EURC_ID , M.MTH_RENT_AMT , RR.RLIEF_ID , CD.CODE_NAME , M.APP_TYPE_ID , I.CODE , G.MEM_CODE , M.ORG_CODE , M.DEPT_CODE , G.MEM_TYPE , J.STK_CTGRY_ID , K.CODE , J.STK_CODE , M.CUST_ID , I.REGN_ID , M.BRNCH_ID , G.BRNCH ) GROUP BY INSTALL_DT ) A , ( /* Interface Table */ SELECT A.INST_DATE AS INSTALL_DT , A.TRAN_STATUS_CD , A.RGST_DT , MAX(A.IF_TYPE) AS IF_TYPE , MAX(B.IF_TYPE_NM) AS IF_TYPE_NM , MAX(B.BIZ_COL) AS BIZ_COL , MAX(B.CHK_COL) AS CHK_COL , COUNT(*) AS RCV_CNT , SUM(A.UNBILL) AS UNBILL FROM ITF0963M A , ITF0001M B WHERE A.IF_TYPE = B.IF_TYPE (+)
AND A.TRAN_STATUS_CD = #{tranStatusCd}
AND SUBSTR(A.INST_DATE,0,6) = #{searchDate} GROUP BY A.INST_DATE,A.TRAN_STATUS_CD,A.RGST_DT ) B WHERE A.INSTALL_DT = B.INSTALL_DT(+) ) ORDER BY ORDER_SEQ

/* Bank Charge */ SELECT * FROM ( SELECT B.IF_TYPE , C.IF_TYPE_NM , B.TRAN_STATUS_CD , B.RGST_DT , C.BIZ_COL , NULL AS SND_CHK_COL, B.BANKAC AS RCV_CHK_COL, C.CHK_COL , NULL AS SND_CHK_VAL , B.ADV_AMT+B.DIFF_AMT AS RCV_CHK_VAL , ROWNUM AS ORDER_SEQ , B.IF_TYPE||B.BANKAC||ROWNUM as ROW_ID FROM ITF0942M B , ITF0001M C WHERE B.IF_TYPE = C.IF_TYPE(+)
AND B.TRAN_STATUS_CD = #{tranStatusCd}
AND SUBSTR(B.PAY_DT,0,6) = #{searchDate} ORDER BY B.BANKAC ) ORDER BY ORDER_SEQ

/* Interface Branch Code */ SELECT * FROM ( SELECT B.IF_TYPE , C.IF_TYPE_NM , B.TRAN_STATUS_CD , B.RGST_DT , C.BIZ_COL , NULL AS SND_CHK_COL, B.BRNCH_CODE AS RCV_CHK_COL, C.CHK_COL , NULL AS SND_CHK_VAL , NULL AS RCV_CHK_VAL , ROWNUM AS ORDER_SEQ , B.IF_TYPE||B.BRNCH_CODE||ROWNUM as ROW_ID FROM ITF0771M B , ITF0001M C WHERE B.IF_TYPE = C.IF_TYPE(+)
AND B.TRAN_STATUS_CD = #{tranStatusCd}
AND SUBSTR(B.RGST_DT,0,6) = #{searchDate} ORDER BY B.BRNCH_CODE ) ORDER BY ORDER_SEQ
