mapper.update:
UPDATE SYS0059M a
     SET a.mymenu_name = #{mymenuName}
         , a.mymenu_order = #{mymenuOrder}
         , a.use_yn = #{useYn}
         , a.upd_user_id = #{userId}
         , a.upd_dt = SYSDATE
 WHERE a.mymenu_code = #{mymenuCode}

mapper.delete:
DELETE 
  FROM SYS0059M a
WHERE a.mymenu_code = #{mymenuCode}

mapper.update:
UPDATE SYS0060D a
     SET a.pgm_ord = #{pgmOrd} 
         , a.upd_user_id = #{userId}
         , a.upd_dt = SYSDATE
 WHERE a.mymenu_code = #{mymenuCode}
     AND a.menu_code = #{menuCode}

mapper.delete:
DELETE 
  FROM SYS0060D a
 WHERE a.mymenu_code = #{mymenuCode}
     AND a.menu_code = #{menuCode}
