mapper.select:
SELECT a.user_id , a.mymenu_code , a.mymenu_name , a.mymenu_order , a.use_yn , a.user_id||a.mymenu_code as row_id /* PK, AUIGrid key row setting */ FROM SYS0059M a WHERE a.user_id = #{userId}
AND a.use_yn = #{useYn}
AND (UPPER(a.mymenu_code) LIKE UPPER(#{mymenuCode}) || '%' OR UPPER(a.mymenu_name) LIKE '%' || UPPER(#{mymenuCode}) || '%')

mapper.update:
UPDATE SYS0059M a
     SET a.mymenu_name = #{mymenuName}
         , a.mymenu_order = #{mymenuOrder}
         , a.use_yn = #{useYn}
         , a.upd_user_id = #{userId}
         , a.upd_dt = SYSDATE
 WHERE a.user_id = #{userId}
     AND a.mymenu_code = #{mymenuCode}

mapper.select:
SELECT a.user_id , a.mymenu_code , d.mymenu_name , a.menu_code , b.menu_name , a.pgm_ord , b.pgm_code , c.pgm_name , a.user_id||a.mymenu_code||a.menu_code as row_id /* PK, AUIGrid key row setting */ FROM SYS0060D a , SYS0051M b , SYS0050M c , SYS0059M d WHERE a.menu_code = b.menu_code AND b.pgm_code = c.pgm_code AND a.mymenu_code = d.mymenu_code AND a.user_id = #{userId}
AND a.mymenu_code = #{mymenuCode}
AND a.menu_code = #{menuCode}

mapper.update:
UPDATE SYS0060D a
     SET a.pgm_ord = #{pgmOrd} 
         , a.upd_user_id = #{userId}
         , a.upd_dt = SYSDATE
 WHERE a.user_id = #{userId}
     AND a.mymenu_code = #{mymenuCode}
     AND a.menu_code = #{menuCode}

mapper.delete:
DELETE 
  FROM SYS0060D a
 WHERE 1=1
     AND a.user_id = #{userId}

     AND a.mymenu_code = #{mymenuCode}

     AND a.menu_code = #{menuCode}
