mapper.update:
	UPDATE SYS0058M
         SET valid_dt_to = #{validDtTo} ,
	           auth_code = #{authCode} ,
	           upd_user_id = #{updUserId},
	           upd_dt = SYSDATE
	 WHERE user_id = #{userId}
	     AND auth_div_code = #{authDivCode}
	     AND auth_code = #{authCode}
	     AND valid_dt_from = #{validDtFrom}

mapper.delete:
	DELETE
	  FROM SYS0058M
	 WHERE user_id = #{userId}
         AND auth_div_code = #{authDivCode}
	     AND auth_code = #{authCode}
	     AND valid_dt_from = #{validDtFrom}
