SELECT F40.BUDGET_CODE, F10.BUDGET_CODE_TEXT, F40.GL_ACC_CODE, F9.GL_ACC_DESC, F40.NATURE_OF_EXP, S38.NAME STUS, S38.STUS_CODE_ID, S13.CODE_NAME CODE_NAME, S13.CODE_ID FROM FCM0040D F40 LEFT JOIN FCM0010M F10 ON F10.BUDGET_CODE = F40.BUDGET_CODE LEFT JOIN FCM0009M F9 ON F9.GL_ACC_CODE = F40.GL_ACC_CODE LEFT JOIN SYS0038M S38 ON S38.STUS_CODE_ID = F40.STUS_CODE_ID LEFT JOIN SYS0013M S13 ON S13.CODE_ID = F40.EXPENSE_TYP AND S13.CODE_MASTER_ID = 552 WHERE 1=1
AND F40.BUDGET_CODE = #{budgetCode}
AND F40.GL_ACC_CODE = #{glAccCode}
AND UPPER(F40.NATURE_OF_EXP) LIKE '%'|| UPPER(#{searchKey}) || '%'
AND F40.STUS_CODE_ID IN
#{item}
