#{item}

SELECT BUDGET_DOC_NO, COST_CENTR, ADJ_YEAR_MONTH,
BUDGET_CODE, GL_ACC_CODE, BUDGET_ADJ_TYPE, SIGNAL, ADJ_AMT, ADJ_REM, BUDGET_GRP_SEQ
FROM FCM0039D
WHERE UPLOAD_NO = #{uploadAdjNo}
AND ADJ_NUMBER = #{adjNumber}
ORDER BY BUDGET_GRP_SEQ ASC

SELECT COUNT(-1) CNT
FROM FCM0101M
WHERE COST_CENTR = #{costCenter}
AND GL_ACC_CODE = #{glAccCode}
AND BUDGET_CODE= #{budgetCode}
AND BUDGET_PLAN_YEAR = SUBSTR(#{adjYearMonth}, 4,4)
AND BUDGET_PLAN_MONTH = TO_NUMBER(SUBSTR(#{adjYearMonth}, 0,2))

SELECT DISTINCT BUDGET_DOC_NO
           FROM FCM0039D
           WHERE UPLOAD_NO = #{uploadAdjNo}
           AND ADJ_NUMBER = #{adjNumber}
           AND BUDGET_DOC_NO IS NOT NULL

SELECT COUNT(BUDGET_DOC_NO)
           FROM FCM0039D
           WHERE UPLOAD_NO = #{uploadAdjNo}
           AND ADJ_NUMBER = #{adjNumber}
           AND BUDGET_DOC_NO IS NOT NULL

SELECT NVL(SUM(ADJ_AMT), 0.0) ADJ_AMT FROM FCM0039D
WHERE UPLOAD_NO = #{uploadAdjNo}
AND ADJ_YEAR_MONTH = #{adjYearMonth}
AND BUDGET_CODE = #{budgetCode}
AND GL_ACC_CODE = #{glAccCode}

AND SIGNAL = '-'
AND ADJ_NUMBER <= #{adjNumber}

ORDER BY ADJ_NUMBER
