mapper.select:
SELECT
MAIN.CONT_TRACK_ID CONT_TRACK_ID ,MAIN.CONT_TRACK_NO CONT_TRACK_NO ,MAIN.CONT_REF_NO CONTRACT_REF_NO ,MAIN.CONT_TYPE ,MAIN.CONT_TYPE_OTHER ,MAIN.CONT_NAME CONTRACT_NAME ,TO_CHAR(MAIN.CONT_DATE,'DD/MM/YYYY') CONT_DATE ,TO_CHAR(MAIN.CRT_DT,'DD/MM/YYYY') CRT_DT ,CUSERN.USER_NAME ,TO_CHAR(MAIN.UPD_DT,'DD/MM/YYYY') UPD_DT ,MAIN.UPD_USER_ID ,MAIN.ATCH_FILE_GRP_ID ,MAIN.CONT_TERM_YEAR ,MAIN.CONT_TERM_MONTH ,TO_CHAR(MAIN.CONT_COMM_DT,'DD/MM/YYYY') COMM_DT ,TO_CHAR(MAIN.CONT_EXPIRY_DT,'DD/MM/YYYY') EXP_DT ,MAIN.CONT_STATUS ,MAIN.IS_OPT_RENEW ,MAIN.RENEWAL_CYCLE ,DET.DEPT_NAME ,DET.DEPT_EMAIL ,DET.PIC_NAME ,DET.PIC_EMAIL ,DET.VEN_NAME ,DET.VEN_NRIC ,DET.VEN_TYPE ,DET.VEN_TYPE_OTHER ,DET.VEN_PIC_NAME ,DET.VEN_PIC_EMAIL ,DET.VEN_PIC_NO
FROM SAL0413M MAIN LEFT JOIN SAL0414D DET ON DET.CONT_TRACK_ID = MAIN.CONT_TRACK_ID LEFT JOIN SYS0047M CUSERN ON CUSERN.USER_ID = MAIN.CRT_USER_ID WHERE MAIN.CONT_TRACK_ID = #{contTrackId}

mapper.update:
        update SAL0415D SET
			RENEWAL_COMM_DT = TO_DATE(#{contractCommDt}, 'DD/MM/YYYY')
			,RENEWAL_EXP_DT = TO_DATE(#{contractCommExpDt}, 'DD/MM/YYYY')
			,RENEWAL_TERM_YEAR = #{contractTermY}
			,RENEWAL_TERM_MONTH = #{contractTermM}
			,RENEWAL_CUTOFF_DT = TO_DATE(#{noticePeriod}, 'DD/MM/YYYY')
			
			,CONT_RENEWAL_DT = TO_DATE(#{contractRenewDt}, 'YYYY/MM/DD')
			
			,ATCH_FILE_GRP_ID = #{atchFileGrpId}
			,UPD_DT = SYSDATE
			,UPD_USER_ID = #{userId}
		WHERE CONT_TRACK_ID = #{contTrackId}
		AND SEQ = #{seq}

update SAL0415D SET
    STUS = #{status}
    ,UPD_DT = SYSDATE
    ,UPD_USER_ID = #{userId}
WHERE CONT_TRACK_ID = #{contTrackId}
AND SEQ = #{seq}

update SAL0413M SET
    RENEWAL_CYCLE = #{noOfCycle}
WHERE CONT_TRACK_ID = #{contTrackId}
