mapper.select:
SELECT B.CRDIT_CARD_SEQ CRC_ID, B.CRDIT_CARD_USER_ID, UPPER(B.CRDIT_CARD_USER_NAME) CRC_HOLDER_NM, B.COST_CENTR CRC_COST_CENTER, B.COST_CENTR_NAME CRC_COST_CENTER_DESC, /* B.CHRG_USER_ID || ' - ' || C.NAME CRC_PIC, */ C.NAME CRC_PIC, SUBSTR(A.CRDIT_CARD_NO, 1, 6) || '******' || SUBSTR(A.CRDIT_CARD_NO, -4) CRC_NO, A.CRC_LIMIT_ADJ_YYYY, SUM(M1) M1, SUM(M2) M2, SUM(M3) M3, SUM(M4) M4, SUM(M5) M5, SUM(M6) M6, SUM(M7) M7, SUM(M8) M8, SUM(M9) M9, SUM(M10) M10, SUM(M11) M11, SUM(M12) M12 FROM ( SELECT * FROM ( SELECT SUM(CASE WHEN A.SIGNAL = '+' THEN A.ADJ_AMT ELSE 0 END) - SUM(CASE WHEN A.SIGNAL = '-' THEN A.ADJ_AMT ELSE 0 END) TOTAL, A.CRDIT_CARD_NO, A.CRC_LIMIT_ADJ_YYYY, A.CRC_LIMIT_ADJ_MM FROM FCM0033D A JOIN FCM0034D B ON A.CRDIT_CARD_ADJ_NO = B.CRDIT_CARD_ADJ_NO AND B.CRDIT_CARD_ADJ_NO IS NOT NULL AND B.APPV_PRCSS_STUS = 'A' WHERE 1=1
AND A.CRC_LIMIT_ADJ_MM >= #{stMonth}
AND A.CRC_LIMIT_ADJ_MM <= #{edMonth}
AND (A.CRC_LIMIT_ADJ_MM >= 1 AND A.CRC_LIMIT_ADJ_MM <= 12)
AND A.CRC_LIMIT_ADJ_YYYY = #{year}
AND A.CRC_LIMIT_ADJ_YYYY = TO_NUMBER(TO_CHAR(SYSDATE, 'YYYY'))
GROUP BY A.CRDIT_CARD_NO, A.CRC_LIMIT_ADJ_YYYY, A.CRC_LIMIT_ADJ_MM ) PIVOT (SUM(TOTAL) FOR CRC_LIMIT_ADJ_MM IN ( '01' AS M1, '02' AS M2, '03' AS M3, '04' AS M4, '05' AS M5, '06' AS M6, '07' AS M7, '08' AS M8, '09' AS M9, '10' AS M10, '11' AS M11, '12' AS M12 ) )
UNION ALL
SELECT CRDIT_CARD_NO, CRC_LIMIT_YYYY, SUM(M1) M1, SUM(M2) M2, SUM(M3) M3, SUM(M4) M4, SUM(M5) M5, SUM(M6) M6, SUM(M7) M7, SUM(M8) M8, SUM(M9) M9, SUM(M10) M10, SUM(M11) M11, SUM(M12) M12 FROM( SELECT CRDIT_CARD_NO, TO_NUMBER(#{year}) AS CRC_LIMIT_YYYY, NVL(CASE WHEN ( TO_DATE(START_DATE,'DD/MM/YYYY') <= TO_DATE('01'||'/'||#{year},'MM/YYYY') AND TO_DATE(END_DATE,'DD/MM/YYYY') >= TO_DATE('01'||'/'||#{year},'MM/YYYY') ) THEN PLAN_LIMIT_AMT ELSE 0 END, 0) M1, NVL(CASE WHEN ( TO_DATE(START_DATE,'DD/MM/YYYY') <= TO_DATE('02'||'/'||#{year},'MM/YYYY') AND TO_DATE(END_DATE,'DD/MM/YYYY') >= TO_DATE('02'||'/'||#{year},'MM/YYYY') ) THEN PLAN_LIMIT_AMT ELSE 0 END, 0) M2, NVL(CASE WHEN ( TO_DATE(START_DATE,'DD/MM/YYYY') <= TO_DATE('03'||'/'||#{year},'MM/YYYY') AND TO_DATE(END_DATE,'DD/MM/YYYY') >= TO_DATE('03'||'/'||#{year},'MM/YYYY') ) THEN PLAN_LIMIT_AMT ELSE 0 END, 0) M3, NVL(CASE WHEN ( TO_DATE(START_DATE,'DD/MM/YYYY') <= TO_DATE('04'||'/'||#{year},'MM/YYYY') AND TO_DATE(END_DATE,'DD/MM/YYYY') >= TO_DATE('04'||'/'||#{year},'MM/YYYY') ) THEN PLAN_LIMIT_AMT ELSE 0 END, 0) M4, NVL(CASE WHEN ( TO_DATE(START_DATE,'DD/MM/YYYY') <= TO_DATE('05'||'/'||#{year},'MM/YYYY') AND TO_DATE(END_DATE,'DD/MM/YYYY') >= TO_DATE('05'||'/'||#{year},'MM/YYYY') ) THEN PLAN_LIMIT_AMT ELSE 0 END, 0) M5, NVL(CASE WHEN ( TO_DATE(START_DATE,'DD/MM/YYYY') <= TO_DATE('06'||'/'||#{year},'MM/YYYY') AND TO_DATE(END_DATE,'DD/MM/YYYY') >= TO_DATE('06'||'/'||#{year},'MM/YYYY') ) THEN PLAN_LIMIT_AMT ELSE 0 END, 0) M6, NVL(CASE WHEN ( TO_DATE(START_DATE,'DD/MM/YYYY') <= TO_DATE('07'||'/'||#{year},'MM/YYYY') AND TO_DATE(END_DATE,'DD/MM/YYYY') >= TO_DATE('07'||'/'||#{year},'MM/YYYY') ) THEN PLAN_LIMIT_AMT ELSE 0 END, 0) M7, NVL(CASE WHEN ( TO_DATE(START_DATE,'DD/MM/YYYY') <= TO_DATE('08'||'/'||#{year},'MM/YYYY') AND TO_DATE(END_DATE,'DD/MM/YYYY') >= TO_DATE('08'||'/'||#{year},'MM/YYYY') ) THEN PLAN_LIMIT_AMT ELSE 0 END, 0) M8, NVL(CASE WHEN ( TO_DATE(START_DATE,'DD/MM/YYYY') <= TO_DATE('09'||'/'||#{year},'MM/YYYY') AND TO_DATE(END_DATE,'DD/MM/YYYY') >= TO_DATE('09'||'/'||#{year},'MM/YYYY') ) THEN PLAN_LIMIT_AMT ELSE 0 END, 0) M9, NVL(CASE WHEN ( TO_DATE(START_DATE,'DD/MM/YYYY') <= TO_DATE('10'||'/'||#{year},'MM/YYYY') AND TO_DATE(END_DATE,'DD/MM/YYYY') >= TO_DATE('10'||'/'||#{year},'MM/YYYY') ) THEN PLAN_LIMIT_AMT ELSE 0 END, 0) M10, NVL(CASE WHEN ( TO_DATE(START_DATE,'DD/MM/YYYY') <= TO_DATE('11'||'/'||#{year},'MM/YYYY') AND TO_DATE(END_DATE,'DD/MM/YYYY') >= TO_DATE('11'||'/'||#{year},'MM/YYYY') ) THEN PLAN_LIMIT_AMT ELSE 0 END, 0) M11, NVL(CASE WHEN ( TO_DATE(START_DATE,'DD/MM/YYYY') <= TO_DATE('12'||'/'||#{year},'MM/YYYY') AND TO_DATE(END_DATE,'DD/MM/YYYY') >= TO_DATE('12'||'/'||#{year},'MM/YYYY') ) THEN PLAN_LIMIT_AMT ELSE 0 END, 0) M12 FROM ( SELECT f0038d.*,f0016m.CRDIT_CARD_NO AS CRDIT_CARD_NO,TO_CHAR(f0038d.START_DATE,'MM') AS CRC_LIMIT_MM,TO_NUMBER(TO_CHAR(f0038d.START_DATE,'YYYY')) AS CRC_LIMIT_YYYY FROM FCM0038D f0038d INNER JOIN FCM0016M f0016m ON f0016m.CRDIT_CARD_SEQ = f0038d.CREDIT_CARD_SEQ WHERE 1=1 AND f0038d.STUS_CODE_ID = 1 AND #{year} >= TO_NUMBER(TO_CHAR(f0038d.START_DATE,'YYYY')) ) ) GROUP BY CRDIT_CARD_NO, CRC_LIMIT_YYYY
UNION ALL
SELECT * FROM ( SELECT CRDIT_CARD_NO, TO_NUMBER(CLM_YEAR) CLM_YEAR, CLM_MM, (SUM(PENDING_AMT) + SUM(UTILIZED_AMT)) * -1 TOTAL FROM ( SELECT A.CRDIT_CARD_NO, SUBSTR(B.CLM_MONTH, 1, 4) CLM_YEAR, TO_NUMBER(SUBSTR(B.CLM_MONTH, -2)) CLM_MM, CASE WHEN (C.APPV_PRCSS_STUS = 'R' OR C.APPV_PRCSS_STUS = 'P') THEN SUM(D.APPV_AMT) ELSE 0 END PENDING_AMT, CASE WHEN C.APPV_PRCSS_STUS = 'A' THEN SUM(D.APPV_AMT) ELSE 0 END UTILIZED_AMT FROM FCM0016M A JOIN FCM0017M B ON A.CRDIT_CARD_USER_ID = B.CRDIT_CARD_USER_ID JOIN FCM0004M C ON B.APPV_PRCSS_NO = C.APPV_PRCSS_NO AND C.APPV_PRCSS_STUS IN ('A','R','P') JOIN FCM0015D D ON C.APPV_PRCSS_NO = D.APPV_PRCSS_NO AND D.EXP_TYPE IN ( SELECT EXP_TYPE FROM FCM0001M WHERE CNTRL_EXP = 'Y' AND CLM_TYPE = 'J3' ) WHERE A.CRDIT_CARD_NO IN (
SELECT DISTINCT CRDIT_CARD_NO FROM FCM0038D f0038d INNER JOIN FCM0016M f0016m ON f0016m.CRDIT_CARD_SEQ = f0038d.CREDIT_CARD_SEQ WHERE 1=1 AND f0038d.STUS_CODE_ID = 1 AND #{year} >= TO_NUMBER(TO_CHAR(f0038d.START_DATE,'YYYY'))
)
AND SUBSTR(B.CLM_MONTH, 1, 4) = #{year}
AND SUBSTR(B.CLM_MONTH, 1, 4) = TO_NUMBER(TO_CHAR(SYSDATE, 'YYYY'))
AND TO_NUMBER(SUBSTR(B.CLM_MONTH, -1)) >= #{stMonth}
AND TO_NUMBER(SUBSTR(B.CLM_MONTH, -1)) <= #{edMonth}
AND (TO_NUMBER(SUBSTR(B.CLM_MONTH, -1)) >= 1 AND TO_NUMBER(SUBSTR(B.CLM_MONTH, -1)) <= 12)
GROUP BY A.CRDIT_CARD_NO, C.APPV_PRCSS_STUS, SUBSTR(B.CLM_MONTH, 1, 4), TO_NUMBER(SUBSTR(B.CLM_MONTH, -2)) ) GROUP BY CRDIT_CARD_NO, CLM_YEAR, CLM_MM ) PIVOT (SUM(TOTAL) FOR CLM_MM IN ( '01' AS M1, '02' AS M2, '03' AS M3, '04' AS M4, '05' AS M5, '06' AS M6, '07' AS M7, '08' AS M8, '09' AS M9, '10' AS M10, '11' AS M11, '12' AS M12 ) ) ) A JOIN FCM0016M B ON A.CRDIT_CARD_NO = B.CRDIT_CARD_NO
AND B.CRDIT_CARD_SEQ IN
#{item}
AND B.CRDIT_CARD_SEQ IN
#{item}
JOIN ORG0001D C ON B.CHRG_USER_ID = C.MEM_CODE JOIN SYS0047M D ON B.CRDIT_CARD_USER_ID = D.HR_CODE
WHERE 1=1 AND (B.CHRG_USER_ID = ( SELECT HR_CODE FROM SYS0047M WHERE USER_ID = #{loginUserId} ) OR D.USER_ID = #{loginUserId})
GROUP BY B.CRDIT_CARD_SEQ, B.CRDIT_CARD_USER_ID, B.CRDIT_CARD_USER_NAME, B.COST_CENTR, B.COST_CENTR_NAME, /* B.CHRG_USER_ID || ' - ' || C.NAME, */ C.NAME, SUBSTR(A.CRDIT_CARD_NO, 1, 6) || '******' || SUBSTR(A.CRDIT_CARD_NO, -4), A.CRC_LIMIT_ADJ_YYYY

SELECT * FROM ( SELECT DISTINCT CASE WHEN C.APPV_PRCSS_STUS IS NULL THEN 'Temporary Save' WHEN C.APPV_PRCSS_STUS = 'R' THEN 'Open' WHEN (C.APPV_PRCSS_STUS = 'A' OR C.APPV_PRCSS_STUS = 'J') THEN 'Close' END ADJ_STUS, A.CRDIT_CARD_ADJ_NO ADJ_NO, NVL(TO_CHAR(C.CRT_DT, 'DD/MM/YYYY'), '-') SUBMISSION_DATE, B.CRDIT_CARD_USER_NAME CRC_NAME, SUBSTR(A.CRDIT_CARD_NO, 1, 6) || '******' || SUBSTR(A.CRDIT_CARD_NO, -4) CRC_NO, LPAD(A.CRC_LIMIT_ADJ_MM, 2, '0') || '/' || TO_CHAR(A.CRC_LIMIT_ADJ_YYYY) PERIOD, CASE WHEN A.ADJ_TYPE = 1 THEN 'Transfer between Credit Card Holder' WHEN A.ADJ_TYPE = 2 THEN 'Transfer between Period' WHEN A.ADJ_TYPE = 3 THEN 'Addition' WHEN A.ADJ_TYPE = 4 THEN 'Deduction' END ADJ_TYPE, A.SIGNAL, A.ADJ_AMT, /* D.HR_CODE || ' - ' || D.USER_NAME REQUESTOR, */ D.USER_NAME REQUESTOR, CASE WHEN C.APPV_PRCSS_STUS IS NULL THEN 'D' ELSE C.APPV_PRCSS_STUS END APPV_STUS, CASE WHEN C.APPV_PRCSS_STUS IS NULL THEN 'Draft' WHEN C.APPV_PRCSS_STUS = 'R' THEN 'Requested' WHEN C.APPV_PRCSS_STUS = 'A' THEN 'Approved' WHEN C.APPV_PRCSS_STUS = 'J' THEN 'Rejected' END APPV_STUS_NAME, A.ADJ_GRP_SEQ, A.CRT_DT FROM FCM0033D A JOIN FCM0016M B ON A.CRDIT_CARD_ID = B.CRDIT_CARD_SEQ LEFT JOIN FCM0034D C ON A.CRDIT_CARD_ADJ_NO = C.CRDIT_CARD_ADJ_NO JOIN SYS0047M D ON A.CRT_USER_ID = D.USER_ID JOIN SYS0047M E ON A.CRDIT_CARD_USER_ID = E.HR_CODE WHERE 1=1
AND A.CRC_LIMIT_ADJ_MM >= TO_NUMBER(SUBSTR(#{frAdjPeriod}, 1, 2)) AND A.CRC_LIMIT_ADJ_YYYY >= TO_NUMBER(SUBSTR(#{frAdjPeriod}, -4)) AND A.CRC_LIMIT_ADJ_MM <= TO_NUMBER(SUBSTR(#{toAdjPeriod}, 1, 2)) AND A.CRC_LIMIT_ADJ_YYYY >= TO_NUMBER(SUBSTR(#{toAdjPeriod}, -4))
AND B.CRDIT_CARD_SEQ IN
#{item}
AND A.ADJ_TYPE IN
#{item}
AND B.COST_CENTR = #{costCenter}
WHERE 1=1 AND (B.CHRG_USER_ID = ( SELECT HR_CODE FROM SYS0047M WHERE USER_ID = #{loginUserId} ) OR E.USER_ID = #{loginUserId})
) WHERE 1=1
AND APPV_STUS IN
#{item}
AND ADJ_NO >= #{frAdjNo}
AND ADJ_NO <= #{toAdjNo}
ORDER BY CRT_DT DESC, ADJ_GRP_SEQ

SELECT * FROM ( SELECT DISTINCT A.CRDIT_CARD_ADJ_NO ADJ_NO, NVL(TO_CHAR(C.CRT_DT, 'DD/MM/YYYY'), '-') SUBMISSION_DATE, B.CRDIT_CARD_USER_NAME CRC_NAME, B.COST_CENTR COST_CENTER, B.COST_CENTR_NAME COST_CENTER_DESC, SUBSTR(A.CRDIT_CARD_NO, 1, 6) || '******' || SUBSTR(A.CRDIT_CARD_NO, -4) CRC_NO, LPAD(A.CRC_LIMIT_ADJ_MM, 2, '0') || '/' || TO_CHAR(A.CRC_LIMIT_ADJ_YYYY) PERIOD, CASE WHEN A.ADJ_TYPE = 1 THEN 'Transfer between Credit Card Holder' WHEN A.ADJ_TYPE = 2 THEN 'Transfer between Period' WHEN A.ADJ_TYPE = 3 THEN 'Addition' WHEN A.ADJ_TYPE = 4 THEN 'Deduction' END ADJ_TYPE, A.ADJ_AMT, D.HR_CODE || ' - ' || D.USER_NAME REQUESTOR, CASE WHEN C.APPV_PRCSS_STUS IS NULL THEN 'D' ELSE C.APPV_PRCSS_STUS END APPV_STUS, CASE WHEN C.APPV_PRCSS_STUS IS NULL THEN 'Draft' WHEN C.APPV_PRCSS_STUS = 'R' THEN 'Requested' WHEN C.APPV_PRCSS_STUS = 'A' THEN 'Approved' WHEN C.APPV_PRCSS_STUS = 'J' THEN 'Rejected' END APPV_STUS_NAME, A.ADJ_REM, A.CRT_DT FROM FCM0033D A JOIN FCM0016M B ON A.CRDIT_CARD_ID = B.CRDIT_CARD_SEQ JOIN FCM0034D C ON A.CRDIT_CARD_ADJ_NO = C.CRDIT_CARD_ADJ_NO AND C.APPV_PRCSS_STUS NOT IN ('A','J') JOIN SYS0047M D ON A.CRT_USER_ID = D.USER_ID WHERE A.ADJ_GRP_SEQ = 1
AND A.CRC_LIMIT_ADJ_MM >= TO_NUMBER(SUBSTR(#{frAdjPeriod}, 1, 2)) AND A.CRC_LIMIT_ADJ_YYYY >= TO_NUMBER(SUBSTR(#{frAdjPeriod}, -4)) AND A.CRC_LIMIT_ADJ_MM <= TO_NUMBER(SUBSTR(#{toAdjPeriod}, 1, 2)) AND A.CRC_LIMIT_ADJ_YYYY >= TO_NUMBER(SUBSTR(#{toAdjPeriod}, -4))
