mapper.select:
SELECT A.CRDIT_CARD_SEQ, A.CRDIT_CARD_USER_ID, A.CRDIT_CARD_USER_NAME, A.CRDIT_CARD_NO, C.APPV_REQ_KEY_NO, C.APPV_PRCSS_STUS, C.CRT_USER_ID, C.REQST_USER_ID AS REQUESTOR, TO_CHAR(C.CRT_DT,'DD/MM/YYYY') AS CRT_DT, TO_CHAR(C.APPV_PRCSS_DT,'DD/MM/YYYY') AS APPV_PRCSS_DT, D.APPV_AMT, D.EXP_DESC FROM FCM0016M A JOIN FCM0017M B ON A.CRDIT_CARD_USER_ID = B.CRDIT_CARD_USER_ID JOIN FCM0004M C ON B.APPV_PRCSS_NO = C.APPV_PRCSS_NO AND C.APPV_PRCSS_STUS IN ('A','R','P') JOIN FCM0015D D ON C.APPV_PRCSS_NO = D.APPV_PRCSS_NO AND D.EXP_TYPE IN ( SELECT EXP_TYPE FROM FCM0001M WHERE CNTRL_EXP = 'Y' AND CLM_TYPE = 'J3' ) WHERE A.CRDIT_CARD_NO IN ( SELECT DISTINCT CRDIT_CARD_NO FROM FCM0038D f0038d INNER JOIN FCM0016M f0016m ON f0016m.CRDIT_CARD_SEQ = f0038d.CREDIT_CARD_SEQ WHERE 1=1
AND #{year} >= TO_NUMBER(TO_CHAR(f0038d.START_DATE, 'YYYY'))
AND f0038d.STUS_CODE_ID = 1
)
AND B.CLM_MONTH = #{yearMonth}
AND C.APPV_PRCSS_STUS IN ('R', 'P') AND A.CRDIT_CARD_SEQ = #{crcId}

SELECT A.CRDIT_CARD_SEQ, A.CRDIT_CARD_USER_ID, A.CRDIT_CARD_USER_NAME, A.CRDIT_CARD_NO, C.APPV_REQ_KEY_NO, C.APPV_PRCSS_STUS, C.CRT_USER_ID, C.REQST_USER_ID AS REQUESTOR, TO_CHAR(C.CRT_DT,'DD/MM/YYYY') AS CRT_DT, TO_CHAR(C.APPV_PRCSS_DT,'DD/MM/YYYY') AS APPV_PRCSS_DT, D.APPV_AMT, D.EXP_DESC FROM FCM0016M A JOIN FCM0017M B ON A.CRDIT_CARD_USER_ID = B.CRDIT_CARD_USER_ID JOIN FCM0004M C ON B.APPV_PRCSS_NO = C.APPV_PRCSS_NO AND C.APPV_PRCSS_STUS IN ('A','R','P') JOIN FCM0015D D ON C.APPV_PRCSS_NO = D.APPV_PRCSS_NO AND D.EXP_TYPE IN ( SELECT EXP_TYPE FROM FCM0001M WHERE CNTRL_EXP = 'Y' AND CLM_TYPE = 'J3' ) WHERE A.CRDIT_CARD_NO IN ( SELECT DISTINCT CRDIT_CARD_NO FROM FCM0038D f0038d INNER JOIN FCM0016M f0016m ON f0016m.CRDIT_CARD_SEQ = f0038d.CREDIT_CARD_SEQ WHERE 1=1
AND #{year} >= TO_NUMBER(TO_CHAR(f0038d.START_DATE, 'YYYY'))
AND f0038d.STUS_CODE_ID = 1
)
AND B.CLM_MONTH = #{yearMonth}
AND C.APPV_PRCSS_STUS IN ('A') AND A.CRDIT_CARD_SEQ = #{crcId}

SELECT A.CRDIT_CARD_NO, A.CRDIT_CARD_USER_ID, B.CRDIT_CARD_ADJ_NO, B.CRC_LIMIT_ADJ_YYYY, B.CRC_LIMIT_ADJ_MM, B.ADJ_TYPE, (CASE B.ADJ_TYPE WHEN 1 THEN 'Transfer between Credit Card Holder' WHEN 2 THEN 'Transfer between Period' WHEN 3 THEN 'Addition' WHEN 4 THEN 'Deduction' END) AS ADJ_TYPE_DESC, B.SIGNAL, B.ADJ_AMT, B.ADJ_REM, TO_CHAR(C.APPV_PRCSS_DT,'DD/MM/YYYY') AS APPV_PRCSS_DT FROM FCM0016M A LEFT JOIN FCM0033D B ON A.CRDIT_CARD_NO = B.CRDIT_CARD_NO AND A.CRDIT_CARD_USER_ID = B.CRDIT_CARD_USER_ID
AND B.CRC_LIMIT_ADJ_YYYY = #{year}
AND B.CRC_LIMIT_ADJ_MM = #{clmMonth}
LEFT JOIN FCM0034D C ON B.CRDIT_CARD_ADJ_NO = C.CRDIT_CARD_ADJ_NO WHERE A.CRDIT_CARD_NO IN ( SELECT DISTINCT CRDIT_CARD_NO FROM FCM0038D f0038d INNER JOIN FCM0016M f0016m ON f0016m.CRDIT_CARD_SEQ = f0038d.CREDIT_CARD_SEQ WHERE 1=1
AND #{year} >= TO_NUMBER(TO_CHAR(f0038d.START_DATE, 'YYYY'))
AND f0038d.STUS_CODE_ID = 1
) AND C.APPV_PRCSS_STUS = 'A' AND A.CRDIT_CARD_SEQ = #{crcId}
