mapper.select:
/* creditCardMapper.selectCrditCardMgmtList */
SELECT T.CRDIT_CARD_SEQ, T.CRDIT_CARD_USER_ID, T.CRDIT_CARD_USER_NAME, T.CRDIT_CARD_NO, T.CHRG_USER_ID, T.CHRG_USER_NAME, T.COST_CENTR, T.COST_CENTR_NAME, T.ATCH_FILE_GRP_ID, T.ATCH_FILE_ID, T.ATCH_FILE_NAME, T.FILE_EXTSN, T.FILE_CNT, T.CRDIT_CARD_STUS_CODE, T.CRDIT_CARD_STUS, T.CRT_DT, T.UPD_DT, T.PLAN_AMT, T.CRC_LIMIT_YYYY, T.CRC_LIMIT_MM, T.CARD_MGMT_LVL, T.CARD_MGMT_LVL_DESC FROM (SELECT T1.CRDIT_CARD_SEQ, T1.CRDIT_CARD_USER_ID, T1.CRDIT_CARD_USER_NAME, ( SUBSTR(T1.CRDIT_CARD_NO, 1, 4) || '********' || SUBSTR(T1.CRDIT_CARD_NO, -4) ) AS CRDIT_CARD_NO, T1.CHRG_USER_ID, T2.NAME AS CHRG_USER_NAME, T1.COST_CENTR, T1.COST_CENTR_NAME, T1.ATCH_FILE_GRP_ID, T3.ATCH_FILE_ID, T3.ATCH_FILE_NAME, T3.FILE_EXTSN, T3.FILE_CNT, T1.CRDIT_CARD_STUS AS CRDIT_CARD_STUS_CODE, ( CASE T1.CRDIT_CARD_STUS WHEN 'A' THEN 'Active' WHEN 'R' THEN 'Removed' END ) AS CRDIT_CARD_STUS, T1.CRT_DT, T1.UPD_DT, T4.PLAN_AMT, T4.CRC_LIMIT_YYYY, T4.CRC_LIMIT_MM, T5.USER_ID AS CARDHOLDER_USER_ID, T1.CARD_MGMT_LVL, T6.CODE_NAME AS CARD_MGMT_LVL_DESC FROM FCM0016M T1 LEFT JOIN ORG0001D T2 ON T1.CHRG_USER_ID = T2.MEM_CODE LEFT JOIN (SELECT * FROM (SELECT Row_number () OVER ( PARTITION BY F1.ATCH_FILE_GRP_ID ORDER BY F1.ATCH_FILE_GRP_ID, F2.ATCH_FILE_ID) AS RNUM, F1.ATCH_FILE_GRP_ID, F2.ATCH_FILE_ID, F2.ATCH_FILE_NAME, F2.FILE_EXTSN, Count (F1.ATCH_FILE_ID) OVER ( PARTITION BY F1.ATCH_FILE_GRP_ID) FILE_CNT FROM SYS0070M F1 LEFT JOIN SYS0071D F2 ON F1.ATCH_FILE_ID = F2.ATCH_FILE_ID) F WHERE F.RNUM = 1) T3 ON T1.ATCH_FILE_GRP_ID = T3.ATCH_FILE_GRP_ID LEFT JOIN (
SELECT f0038d.*, f0038d.PLAN_LIMIT_AMT AS PLAN_AMT, f0016m.CRDIT_CARD_NO AS CRDIT_CARD_NO,TO_CHAR(f0038d.START_DATE,'MM') AS CRC_LIMIT_MM, TO_NUMBER(TO_CHAR(f0038d.START_DATE,'YYYY')) AS CRC_LIMIT_YYYY, f0016m.CRDIT_CARD_USER_ID FROM FCM0038D f0038d INNER JOIN FCM0016M f0016m ON f0016m.CRDIT_CARD_SEQ = f0038d.CREDIT_CARD_SEQ WHERE 1=1 AND f0038d.STUS_CODE_ID = 1 AND TO_DATE(TO_CHAR(START_DATE, 'DD/MM/YYYY'),'DD/MM/YYYY') <= TO_DATE(TO_CHAR(SYSDATE, 'DD/MM/YYYY'),'DD/MM/YYYY') AND TO_DATE(TO_CHAR(END_DATE, 'DD/MM/YYYY'),'DD/MM/YYYY') >= TO_DATE(TO_CHAR(SYSDATE, 'DD/MM/YYYY'),'DD/MM/YYYY') ) T4 ON T1.CRDIT_CARD_NO = T4.CRDIT_CARD_NO AND T1.CRDIT_CARD_USER_ID = T4.CRDIT_CARD_USER_ID LEFT JOIN SYS0047M T5 ON T1.CRDIT_CARD_USER_ID = T5.HR_CODE LEFT JOIN SYS0013M T6 ON T1.CARD_MGMT_LVL = T6.CODE_ID
WHERE T1.CRT_USER_ID = #{loginUserId} OR T5.USER_ID = #{loginUserId}
) T WHERE 1=1
AND T.UPD_DT >= TO_DATE(#{startDt}, 'DD/MM/YYYY')
AND T.UPD_DT < TO_DATE(#{endDt}, 'DD/MM/YYYY') + 1
AND T.CRDIT_CARD_USER_ID LIKE '%' || #{crditCardUserId} || '%'
AND UPPER(T.COST_CENTR) = UPPER(#{costCenter})
AND T.CHRG_USER_ID LIKE '%' || #{chrgUserId} || '%'
AND T.CRDIT_CARD_NO LIKE '%' || #{crditCardNo} || '%'
AND T.CRDIT_CARD_STUS_CODE IN
#{item}
AND T.CARD_MGMT_LVL IN
#{item}
ORDER BY T.CRT_DT, T.UPD_DT

