mapper.select:
SELECT A.CRDIT_CARD_SEQ, A.CRDIT_CARD_USER_ID, D.CRDIT_CARDHOLDER_NM, A.CRDIT_CARD_NO, D.COST_CENTER, D.COST_CENTER_TEXT COST_CENTER_NAME, A.CREDIT_LIMIT CREDIT_LIMIT, NVL(B.TOTAL_ADJ_AMT, 0) ADJ_AMT, NVL(C.PENDING_AMT, 0) PENDING_AMT, NVL(C.UTILIZED_AMT, 0) UTILIZED_AMT, A.CREDIT_LIMIT + NVL(B.PLUS_ADJ_AMT, 0) - NVL(B.MINUS_ADJ_AMT, 0) - NVL(C.PENDING_AMT, 0) - NVL(C.UTILIZED_AMT, 0) AVAILABLE_AMT FROM ( SELECT B.CRDIT_CARD_SEQ,
B.CRDIT_CARD_USER_ID, B.CRDIT_CARD_NO, A.PLAN_LIMIT_AMT CREDIT_LIMIT
FROM FCM0038D A, FCM0016M B WHERE A.CREDIT_CARD_SEQ = B.CRDIT_CARD_SEQ
AND TO_DATE(TO_CHAR(START_DATE, 'DD/MM/YYYY'),'DD/MM/YYYY') <= TO_DATE(#{month}||'/'||#{year},'MM/YYYY') AND TO_DATE(TO_CHAR(END_DATE, 'DD/MM/YYYY'),'DD/MM/YYYY') >= TO_DATE(#{month}||'/'||#{year},'MM/YYYY')
AND A.STUS_CODE_ID = 1 ) A LEFT JOIN ( SELECT SUM(CASE WHEN B.SIGNAL = '+' THEN B.ADJ_AMT ELSE 0 END) PLUS_ADJ_AMT, SUM(CASE WHEN B.SIGNAL = '-' THEN B.ADJ_AMT ELSE 0 END) MINUS_ADJ_AMT, SUM(CASE WHEN B.SIGNAL = '+' THEN B.ADJ_AMT ELSE 0 END) - SUM(CASE WHEN B.SIGNAL = '-' THEN B.ADJ_AMT ELSE 0 END) TOTAL_ADJ_AMT, A.CRDIT_CARD_NO, A.CRDIT_CARD_USER_ID, B.CRC_LIMIT_ADJ_YYYY, B.CRC_LIMIT_ADJ_MM FROM FCM0016M A LEFT JOIN FCM0033D B ON A.CRDIT_CARD_NO = B.CRDIT_CARD_NO AND A.CRDIT_CARD_USER_ID = B.CRDIT_CARD_USER_ID
AND B.CRC_LIMIT_ADJ_YYYY = #{year}
AND B.CRC_LIMIT_ADJ_MM = #{month}
WHERE A.CRDIT_CARD_NO IN (
SELECT DISTINCT CRDIT_CARD_NO FROM FCM0038D f0038d INNER JOIN FCM0016M f0016m ON f0016m.CRDIT_CARD_SEQ = f0038d.CREDIT_CARD_SEQ WHERE 1=1 AND f0038d.STUS_CODE_ID = 1
AND TO_NUMBER(#{year}) >= TO_NUMBER(TO_CHAR(f0038d.START_DATE, 'YYYY'))
) AND B.CURR_APPV_PRCSS_STUS = 'A' GROUP BY A.CRDIT_CARD_NO, A.CRDIT_CARD_USER_ID, B.CRC_LIMIT_ADJ_YYYY, B.CRC_LIMIT_ADJ_MM ) B ON A.CRDIT_CARD_NO = B.CRDIT_CARD_NO AND A.CRDIT_CARD_USER_ID = B.CRDIT_CARD_USER_ID LEFT JOIN ( SELECT CRDIT_CARD_SEQ, CRDIT_CARD_USER_ID, CRDIT_CARD_NO, SUM(PENDING_AMT) PENDING_AMT, SUM(UTILIZED_AMT) UTILIZED_AMT FROM ( SELECT A.CRDIT_CARD_SEQ, A.CRDIT_CARD_USER_ID, A.CRDIT_CARD_NO, CASE WHEN (C.APPV_PRCSS_STUS = 'R' OR C.APPV_PRCSS_STUS = 'P') THEN SUM(D.APPV_AMT) ELSE 0 END PENDING_AMT, CASE WHEN C.APPV_PRCSS_STUS = 'A' THEN SUM(D.APPV_AMT) ELSE 0 END UTILIZED_AMT FROM FCM0016M A JOIN FCM0017M B ON A.CRDIT_CARD_USER_ID = B.CRDIT_CARD_USER_ID AND A.CRDIT_CARD_NO = B.CRDIT_CARD_NO JOIN FCM0004M C ON B.APPV_PRCSS_NO = C.APPV_PRCSS_NO AND C.APPV_PRCSS_STUS IN ('A','R','P') JOIN FCM0015D D ON C.APPV_PRCSS_NO = D.APPV_PRCSS_NO AND D.EXP_TYPE IN ( SELECT EXP_TYPE FROM FCM0001M WHERE CNTRL_EXP = 'Y' AND CLM_TYPE = 'J3' ) WHERE A.CRDIT_CARD_NO IN (
SELECT DISTINCT CRDIT_CARD_NO FROM FCM0038D f0038d INNER JOIN FCM0016M f0016m ON f0016m.CRDIT_CARD_SEQ = f0038d.CREDIT_CARD_SEQ WHERE 1=1 AND f0038d.STUS_CODE_ID = 1
AND TO_NUMBER(#{year}) >= TO_NUMBER(TO_CHAR(f0038d.START_DATE, 'YYYY'))
)
AND B.CLM_MONTH = #{clmMonth}
GROUP BY A.CRDIT_CARD_SEQ, A.CRDIT_CARD_USER_ID, A.CRDIT_CARD_NO, C.APPV_PRCSS_STUS ) GROUP BY CRDIT_CARD_SEQ, CRDIT_CARD_USER_ID, CRDIT_CARD_NO ) C ON A.CRDIT_CARD_SEQ = C.CRDIT_CARD_SEQ AND A.CRDIT_CARD_USER_ID = C.CRDIT_CARD_USER_ID JOIN ( SELECT A.CRDIT_CARD_SEQ, A.CRDIT_CARD_USER_NAME CRDIT_CARDHOLDER_NM, B.COST_CENTER, B.COST_CENTER_TEXT FROM FCM0016M A, FCM0008M b WHERE A.COST_CENTR = b.COST_CENTER ) D ON A.CRDIT_CARD_SEQ = D.CRDIT_CARD_SEQ WHERE A.CRDIT_CARD_SEQ = #{crcId}

SELECT NVL(SUM(B.NET_AMT),0) CNTRL_SPENT_AMT
FROM FCM0017M A
LEFT JOIN FCM0018D B ON A.CLM_NO = B.CLM_NO
LEFT JOIN FCM0004M C ON A.APPV_PRCSS_NO = C.APPV_PRCSS_NO
WHERE A.CRDIT_CARD_NO = #{cardNo}
AND A.CRDIT_CARD_USER_ID = #{userid}
AND A.CLM_MONTH = #{clmMonth}
AND B.EXP_TYPE in (SELECT EXP_TYPE FROM FCM0001M WHERE CLM_TYPE = 'J3' AND CNTRL_EXP = 'Y')
AND C.APPV_PRCSS_STUS = 'A'

	SELECT USER_ID, USER_NAME, HR_CODE AS MEM_CODE
	FROM SYS0047M WHERE HR_CODE = #{memCode}
    FETCH FIRST 1 ROWS ONLY

