mapper.update:
UPDATE FCM0041M
SET STUS = 117, SUBMIT_DT = SYSDATE
WHERE REQUEST_ID = #{id}

UPDATE FCM0044D
SET STUS = #{stus}, UPD_DT = SYSDATE, REMARK = #{remark}
WHERE MEM_ID = #{memId} AND REQUEST_ID = #{requestId}

UPDATE FCM0041M
SET STUS = #{stus}, ASSIGN_USER = #{assignMemId}
WHERE REQUEST_ID = #{requestId}

UPDATE FCM0041M
RCIV_ATCH_ID = #{rciv}, ADD_ATCH_ID = #{add}, TITLE = #{ePRTitle}, CRT_DT = SYSDATE, COST_CENTER = #{costCenterCode}, REMARK = #{ePRRemark}
WHERE REQUEST_ID = #{requestId}

mapper.select:
SELECT ASSIGN_USER A, RCIV_ATCH_ID F
FROM FCM0041M
WHERE REQUEST_ID = #{id}

mapper.update:
UPDATE FCM0041M
SET RCIV_ATCH_ID = #{rciv}
WHERE REQUEST_ID = #{id}

UPDATE FCM0041M
SET ASSIGN_USER = #{newAssign}
WHERE REQUEST_ID = #{id}

mapper.select:
SELECT CONCAT('PR', LPAD(A.REQUEST_ID, 5, '0')) EPR_NO, A.TITLE, A.COST_CENTER, B.COST_CENTER_TEXT, C.USER_NAME, A.CRT_DT, D.CODE FROM FCM0041M A JOIN FCM0008M B ON A.COST_CENTER = B.COST_CENTER JOIN SYS0047M C ON A.CRT_USER_ID = C.USER_ID JOIN SYS0038M D ON D.STUS_CODE_ID = A.STUS WHERE (A.CRT_USER_ID = #{curr} OR ( A.STUS IN (117, 44, 10, 5, 6) AND EXISTS( SELECT 1 FROM FCM0044D Z JOIN ORG0001D X ON Z.MEM_ID = X.MEM_ID JOIN SYS0047M C ON X.MEM_CODE = C.HR_CODE WHERE Z.REQUEST_ID = A.REQUEST_ID AND C.USER_ID = #{curr} ) ) OR ( A.STUS IN (44, 10, 5, 6) AND EXISTS( SELECT 1 FROM SYS0047M WHERE HR_CODE IN ('P0018', 'P2217', 'P2098', 'P2513', 'P2135') AND USER_ID = #{curr} UNION SELECT 1 FROM DUAL WHERE #{curr} = 217 ) ))
AND A.REQUEST_ID = #{ePRNo}
AND A.STUS = #{ePRStus}
AND TRUNC(A.CRT_DT) >= TO_DATE(#{start}, 'DD/MM/YYYY')
AND TRUNC(A.CRT_DT) <= TO_DATE(#{end}, 'DD/MM/YYYY')
AND A.ASSIGN_USER = #{pic}
AND C.USER_NAME = #{crt}

mapper.update:
UPDATE FCM0041M
SET STUS = 44
WHERE REQUEST_ID = #{requestId}
