mapper.select:
SELECT CLM_TYPE , FN_GET_CODENAME('343', CLM_TYPE) CLM_TYPE_NAME , EXP_TYPE , EXP_TYPE_NAME , GL_ACC_CODE , GL_ACC_CODE_NAME , BUDGET_CODE , BUDGET_CODE_NAME , DISAB_FLAG , TO_CHAR(DISAB_DT, 'dd-mm-yyyy') DISAB_DT , DISAB_USER_ID , TO_CHAR(CRT_DT, 'dd-mm-yyyy') CRT_DT , (SELECT USER_FULL_NAME FROM SYS0047M WHERE USER_ID = CRT_USER_ID) AS CRT_USER_ID , TO_CHAR(UPD_DT, 'dd-mm-yyyy') UPD_DT , (SELECT USER_FULL_NAME FROM SYS0047M WHERE USER_ID = UPD_USER_ID) AS UPD_USER_ID , CNTRL_EXP , DISAB_FLAG FROM FCM0001M WHERE DISAB_FLAG IS NULL
AND CLM_TYPE IN
#{item}
AND CLM_TYPE = #{popClaimType}
AND CLM_TYPE = #{claimTypeCombo}
AND EXP_TYPE IN
#{item}
ORDER BY CLM_TYPE, EXP_TYPE

SELECT #{clmType} || NVL(TO_CHAR(MAX(REPLACE(EXP_TYPE, #{clmType}, '')) + 1, 'FM000'), '001') EXP_TYPE
FROM FCM0001M
WHERE CLM_TYPE = #{clmType}

mapper.insert:
INSERT INTO FCM0001M (
    CLM_TYPE
  , EXP_TYPE
  , EXP_TYPE_NAME
  , GL_ACC_CODE
  , GL_ACC_CODE_NAME
  , BUDGET_CODE
  , BUDGET_CODE_NAME
  , CRT_DT
  , CRT_USER_ID
  , UPD_DT
  , UPD_USER_ID
  , CNTRL_EXP
) values (
    #{clmType}
  , #{expType}
  , #{expTypeName}
  , #{glAccCode}
  , #{glAccCodeName}
  , #{budgetCode}
  , #{budgetCodeName}
  , SYSDATE
  , #{userId}
  , SYSDATE
  , #{userId}
  , #{cntrlExp}
)

mapper.update:
UPDATE FCM0001M
    SET
    
        DISAB_FLAG = #{disabFlag}
      , DISAB_USER_ID = #{userId}
      , DISAB_DT = SYSDATE
    
    
        GL_ACC_CODE = #{glAccCode}
      , GL_ACC_CODE_NAME = #{glAccCodeName}
      , BUDGET_CODE = #{budgetCode}
      , BUDGET_CODE_NAME = #{budgetCodeName}
    
    
      , CNTRL_EXP = #{cntrlFlag}
    
      , UPD_USER_ID = #{userId}
      , UPD_DT = SYSDATE
WHERE CLM_TYPE = #{clmType}
    AND EXP_TYPE = #{expType}

mapper.select:
SELECT CODE_ID, CODE, CODE_NAME, DBMS_LOB.SUBSTR (CODE_DESC, 4000) DESCRIPTION FROM SYS0013M WHERE DISAB = 0 AND CODE != 'J1' AND CODE != 'R1'
AND CODE_MASTER_ID = #{groupCode}
AND CODE = #{Codeval}
AND CODE LIKE #{likeValue}||'%'
AND CODE NOT LIKE #{notlike}||'%'
AND CODE IN
#{item}
ORDER BY ${orderValue}
ORDER BY CODE_NAME

SELECT CLM_TYPE , FN_GET_CODENAME('343', CLM_TYPE) CLM_TYPE_NAME , EXP_TYPE , EXP_TYPE_NAME , GL_ACC_CODE , GL_ACC_CODE_NAME , BUDGET_CODE , BUDGET_CODE_NAME , DISAB_FLAG , TO_CHAR(DISAB_DT, 'dd-mm-yyyy') DISAB_DT , DISAB_USER_ID , TO_CHAR(CRT_DT, 'dd-mm-yyyy') CRT_DT , (SELECT USER_FULL_NAME FROM SYS0047M WHERE USER_ID = CRT_USER_ID) AS CRT_USER_ID , TO_CHAR(UPD_DT, 'dd-mm-yyyy') UPD_DT , (SELECT USER_FULL_NAME FROM SYS0047M WHERE USER_ID = UPD_USER_ID) AS UPD_USER_ID , CNTRL_EXP , DISAB_FLAG FROM FCM0001M WHERE 1=1
AND CLM_TYPE IN
#{item}
AND CLM_TYPE = #{popClaimType}
AND CLM_TYPE = #{claimTypeCombo}
AND EXP_TYPE IN
#{item}
AND EXP_TYPE = #{expenseCd}
AND BUDGET_CODE = #{budgetCd}
AND GL_ACC_CODE = #{glAcc}
AND DISAB_FLAG = 'N' OR DISAB_FLAG IS NULL
AND DISAB_FLAG = 'Y'
ORDER BY CLM_TYPE, EXP_TYPE
