mapper.select:
SELECT CLM_TYPE ,FN_GET_CODENAME('343', CLM_TYPE) CLM_TYPE_NAME ,EXP_TYPE ,EXP_TYPE_NAME ,GL_ACC_CODE ,GL_ACC_CODE_NAME ,BUDGET_CODE ,BUDGET_CODE_NAME ,DISAB_FLAG ,TO_CHAR(DISAB_DT, 'dd-mm-yyyy') DISAB_DT ,DISAB_USER_ID ,TO_CHAR(CRT_DT, 'dd-mm-yyyy') CRT_DT ,CRT_USER_ID ,TO_CHAR(UPD_DT, 'dd-mm-yyyy') UPD_DT ,UPD_USER_ID FROM FCM0001M WHERE DISAB_FLAG is null
AND CLM_TYPE IN
#{item}
AND CLM_TYPE = #{popClaimType}
AND EXP_TYPE IN
#{item}
ORDER BY CLM_TYPE, EXP_TYPE

 SELECT #{clmType} || NVL(TO_CHAR(MAX(REPLACE(EXP_TYPE, #{clmType}, '')) + 1, 'FM000'), '001') EXP_TYPE 
  FROM FCM0001M
WHERE CLM_TYPE = #{clmType}

mapper.update:
UPDATE FCM0001M
   SET  
   
         DISAB_FLAG = #{disabFlag}            
         ,DISAB_USER_ID   = #{userId}
         ,DISAB_DT        = SYSDATE
   
   
          GL_ACC_CODE = #{glAccCode}
         ,GL_ACC_CODE_NAME = #{glAccCodeName}
         ,BUDGET_CODE = #{budgetCode}
         ,BUDGET_CODE_NAME = #{budgetCodeName}
     
         ,UPD_USER_ID   = #{userId}
         ,UPD_DT        = SYSDATE
 WHERE  CLM_TYPE     = #{clmType}
   AND  EXP_TYPE       = #{expType}
