mapper.select:
SELECT A.BATCH_ID, CASE WHEN B.APPV_PRCSS_STUS = 'P' THEN 'Approval-In-Progress' WHEN B.APPV_PRCSS_STUS = 'A' THEN 'Approved' WHEN B.APPV_PRCSS_STUS = 'J' THEN 'Rejected' WHEN B.APPV_PRCSS_STUS = 'R' THEN 'Request' END BATCH_STUS_ID, B.APPV_PRCSS_STUS, A.TOTAL_CLAIMS, TO_CHAR(A.CRT_DT, 'DD-MM-YYYY') CRT_DT, D.USER_NAME CRT_USER, CASE WHEN B.APPV_PRCSS_STUS NOT IN ('A', 'J') THEN '-' ELSE TO_CHAR(B.APPV_PRCSS_DT, 'DD-MM-YYYY') END CNFM_DT, CASE WHEN B.APPV_PRCSS_STUS NOT IN ('A', 'J') THEN '-' ELSE E.USER_NAME END CNFM_USER_ID, ATCH_FILE_GRP_ID, A.APPV_PRCSS_NO FROM FCM0025M A INNER JOIN FCM0004M B ON A.APPV_PRCSS_NO = B.APPV_PRCSS_NO INNER JOIN FCM0026D C ON A.BATCH_ID = C.BATCH_ID LEFT JOIN SYS0047M D ON A.CRT_USER_ID = D.USER_ID LEFT JOIN SYS0047M E ON B.UPD_USER_ID = E.USER_ID WHERE 1=1
AND A.BATCH_ID >= #{bchIdStart}
AND A.BATCH_ID <= #{bchIdEnd}
AND B.APPV_PRCSS_STUS IN
#{item}
AND A.CRT_DT >= TO_CHAR(#{startDt}, 'DD/MM/YYYY')
AND A.CRT_DT <= TO_CHAR(#{endDt}, 'DD/MM/YYYY')
GROUP BY A.BATCH_ID, B.APPV_PRCSS_STUS, CASE WHEN B.APPV_PRCSS_STUS = 'P' THEN 'Approval-In-Progress' WHEN B.APPV_PRCSS_STUS = 'A' THEN 'Approved' WHEN B.APPV_PRCSS_STUS = 'J' THEN 'Rejected' WHEN B.APPV_PRCSS_STUS = 'R' THEN 'Request' END, A.TOTAL_CLAIMS, TO_CHAR(A.CRT_DT, 'DD-MM-YYYY'), D.USER_NAME, CASE WHEN B.APPV_PRCSS_STUS NOT IN ('A', 'J') THEN '-' ELSE TO_CHAR(B.APPV_PRCSS_DT, 'DD-MM-YYYY') END, CASE WHEN B.APPV_PRCSS_STUS NOT IN ('A', 'J') THEN '-' ELSE E.USER_NAME END, ATCH_FILE_GRP_ID, A.APPV_PRCSS_NO ORDER BY A.BATCH_ID

mapper.update:
UPDATE FCM0005D SET
    APPV_STUS = #{appvStus},
    APPV_DT = SYSDATE,
    REJCT_RESN = #{rejctResn},
    UPD_DT = SYSDATE,
    UPD_USER_ID = #{userId}
WHERE APPV_PRCSS_NO = #{appvPrcssNo}
AND APPV_LINE_SEQ = #{appvLineSeq}

mapper.select:
SELECT A.BATCH_ID, CASE WHEN B.APPV_PRCSS_STUS = 'P' THEN 'Approval-In-Progress' WHEN B.APPV_PRCSS_STUS = 'A' THEN 'Approved' WHEN B.APPV_PRCSS_STUS = 'J' THEN 'Rejected' WHEN B.APPV_PRCSS_STUS = 'R' THEN 'Request' END BATCH_STUS_ID, C.CLM_NO,
D.USER_NAME CRT_USER, C.COST_CENTER || ' - ' || C.COST_CENTER_NAME COST_CENTER, C.SUPPLIER || ' - ' || C.SUPPLIER_NAME SUPPLIER,
C.SUPPLIER, C.SUPPLIER_NAME, C.COST_CENTER, C.COST_CENTER_NAME,
C.INVC_NO, TO_CHAR(C.INVC_DT, 'DD-MM-YYYY') INVC_DT, TO_CHAR(C.PAY_DT, 'DD-MM-YYYY') PAY_DT, TO_CHAR(B.REQST_DT, 'DD-MM-YYYY') REQST_DT, B.REQST_USER_ID, C.BUDGET_CODE, C.BUDGET_NAME, C.GL_ACC_NO, C.GL_ACC_NAME, C.AMT, C.UTIL_NO, C.JPAY_NO, TO_CHAR(C.BILL_PERIOD_FR, 'DD-MM-YYYY') BILL_PERIOD_FR, TO_CHAR(C.BILL_PERIOD_TO, 'DD-MM-YYYY') BILL_PERIOD_TO, C.EXP_DESC, TO_CHAR(B.APPV_PRCSS_DT, 'DD/MM/YYYY') APPR_PRCSS_DT FROM FCM0025M A INNER JOIN FCM0004M B ON A.APPV_PRCSS_NO = B.APPV_PRCSS_NO INNER JOIN FCM0026D C ON A.BATCH_ID = C.BATCH_ID LEFT JOIN SYS0047M D ON A.CRT_USER_ID = D.USER_ID LEFT JOIN SYS0047M E ON B.UPD_USER_ID = E.USER_ID WHERE 1=1
AND C.CLM_NO >= #{clmNoStart}
AND C.CLM_NO <= #{clmNoEnd}
AND C.INVC_NO LIKE '%' || UPPER(#{invcNo}) || '%'
AND C.COST_CENTER = #{costCenter}
AND C.SUPPLIER = #{memAccId}
AND C.UTIL_NO LIKE '%' || UPPER(#{utilNo}) || '%'
AND B.APPV_PRCSS_STUS IN
#{item}
AND A.CRT_DT >= TO_CHAR(#{appStartDt}, 'DD/MM/YYYY')
AND A.CRT_DT <= TO_CHAR(#{appEndDt}, 'DD/MM/YYYY')
ORDER BY A.BATCH_ID, C.CLM_NO, C.COST_CENTER, C.SUPPLIER, C.UTIL_NO
