mapper.insert:
/* pettyCashMapper.insertCustodian */
   INSERT INTO FCM0011M
   (
        COST_CENTR
       ,CUSTDN
       ,HEAD_DEPT_FLAG
       ,BANK_CODE
       ,BANK_ACC_NO
       ,CUSTDN_NRIC
       ,APPV_CASH_AMT
       ,ATCH_FILE_GRP_ID
       ,CUSTDN_REM
       ,CRT_DT
       ,CRT_USER_ID
       ,UPD_DT
       ,UPD_USER_ID
    )
    values
    (
        #{costCentr}
       ,#{memAccId}
       ,#{headDeptFlag}
       ,#{bankCode}
       ,#{bankAccNo}
       ,#{custdnNric}
       ,#{appvCashAmt}
       ,#{fileGroupKey}
       ,#{custdnRem}
       ,SYSDATE
       ,#{userId}
       ,SYSDATE
       ,#{userId}
    )

mapper.update:
/* pettyCashMapper.updateCustodian */
   UPDATE FCM0011M
   SET APPV_CASH_AMT = #{appvCashAmt}
       ,CUSTDN_REM = #{custdnRem}
       ,UPD_DT = SYSDATE
       ,UPD_USER_ID = #{userId}
    WHERE COST_CENTR = #{costCentr} AND CUSTDN = #{memAccId}

