mapper.select:
/* pettyCashMapper.selectExpenseList */ SELECT T.CLM_NO, T.COST_CENTR, T.COST_CENTR_NAME, T.MEM_ACC_ID, T.MEM_ACC_NAME, T.CLM_MONTH, T.REQST_DT, 'MYR' AS CUR, T.TOT_AMT, T.APPV_PRCSS_STUS_CODE, T.APPV_PRCSS_STUS, T.APPV_PRCSS_DT FROM (SELECT T1.CLM_NO, T1.COST_CENTR, T1.COST_CENTR_NAME, T1.MEM_ACC_ID, T2.MEM_ACC_NAME, TO_CHAR(TO_DATE(T1.CLM_MONTH, 'YYYYMM'), 'MM/YYYY') AS CLM_MONTH, T3.REQST_DT, T1.TOT_AMT, Nvl (T3.APPV_PRCSS_STUS, 'T') AS APPV_PRCSS_STUS_CODE, Nvl (( CASE T3.APPV_PRCSS_STUS WHEN 'R' THEN 'Request' WHEN 'P' THEN 'Approve In-Progress' WHEN 'A' THEN 'Approved' WHEN 'J' THEN 'Rejected' END ), 'Temporary Save') AS APPV_PRCSS_STUS, T3.APPV_PRCSS_DT FROM FCM0013M T1 LEFT JOIN FCM0006M T2 ON T1.MEM_ACC_ID = T2.MEM_ACC_ID LEFT JOIN FCM0004M T3 ON T1.APPV_PRCSS_NO = T3.APPV_PRCSS_NO) T WHERE 1 = 1
AND T.CLM_MONTH = #{clmMonth}
AND T.MEM_ACC_ID LIKE '%' || #{memAccID} || '%'
AND T.COST_CENTR LIKE '%' || #{costCenter} || '%'
AND T.APPV_PRCSS_STUS_CODE IN
#{item}
ORDER BY T.CLM_NO, T.CLM_MONTH, T.REQST_DT

