mapper.update:
/* scmActivityFundMapper.updateScmActivityFundExp */
   UPDATE FCM0019M
   SET COST_CENTR = #{costCentr}
       ,COST_CENTR_NAME = #{costCentrName}
       ,MEM_ACC_ID = #{memAccId}
       ,BANK_CODE = #{bankCode}
       ,BANK_ACC_NO = #{bankAccNo}
       ,CLM_MONTH = TO_CHAR(TO_DATE(#{clmMonth}, 'MM/YYYY'), 'YYYYMM')
       ,TOT_AMT = #{allTotAmt}
       ,UPD_DT = SYSDATE
       ,UPD_USER_ID = #{userId}
    WHERE CLM_NO = #{clmNo}

/* scmActivityFundMapper.updateScmActivityFundExpItem */
   UPDATE FCM0020D
   SET INVC_DT = TO_DATE(#{invcDt}, 'DD/MM/YYYY')
       ,INVC_TYPE = #{invcType}
       ,INVC_NO = #{invcNo}
       ,SUPPLIR = #{supplirName}
       ,GST_RGIST_NO = #{gstRgistNo}
       ,INVC_REM = #{invcRem}
       ,EXP_TYPE = #{expType}
       ,EXP_TYPE_NAME = #{expTypeName}
       ,GL_ACC_CODE = #{glAccCode}
       ,GL_ACC_CODE_NAME = #{glAccCodeName}
       ,BUDGET_CODE = #{budgetCode}
       ,BUDGET_CODE_NAME = #{budgetCodeName}
       ,TAX_CODE = #{taxCode}
       ,NET_AMT = #{gstBeforAmt}
       ,TAX_AMT = #{gstAmt}
       ,TOT_AMT = #{totAmt}
       ,EXP_DESC = #{expDesc}
       ,UPD_DT = SYSDATE
       ,UPD_USER_ID = #{userId}
    WHERE CLM_NO = #{clmNo}     
    AND CLM_SEQ = #{clmSeq}

mapper.insert:
/* scmActivityFundMapper.insertApproveItems */

   INSERT INTO FCM0015D
   (
        APPV_PRCSS_NO
       ,APPV_ITM_SEQ
       ,INVC_NO
       ,INVC_DT
       ,INVC_TYPE
       ,MEM_ACC_ID
       ,PAY_DUE_DT
       ,EXP_TYPE
       ,EXP_TYPE_NAME
       ,COST_CENTR
       ,COST_CENTR_NAME
       ,GL_ACC_CODE
       ,GL_ACC_CODE_NAME
       ,BUDGET_CODE
       ,BUDGET_CODE_NAME
       ,TAX_CODE
       ,NET_AMT
       ,TAX_AMT
       ,APPV_AMT
       ,EXP_DESC
       ,ATCH_FILE_GRP_ID
       ,CRT_DT
       ,CRT_USER_ID
       ,UPD_DT
       ,UPD_USER_ID
    )
    values
    (
        #{appvPrcssNo}
       ,#{appvItmSeq}
       ,#{invcNo}
       ,NVL(TO_DATE(#{invcDt}, 'DD/MM/YYYY'), SYSDATE)
       ,#{invcType}
       ,#{supplirName}
       ,TO_DATE(#{payDueDt}, 'DD/MM/YYYY')
       ,#{expType}
       ,#{expTypeName}
       ,#{costCentr}
       ,#{costCentrName}
       ,#{glAccCode}
       ,#{glAccCodeName}
       ,#{budgetCode}
       ,#{budgetCodeName}
       ,#{taxCode}
       ,#{gstBeforAmt}
       ,#{gstAmt}
       ,#{totAmt}
       ,#{expDesc}
       ,#{atchFileGrpId}
       ,SYSDATE
       ,#{userId}
       ,SYSDATE
       ,#{userId}
    )

mapper.update:
/* scmActivityFundMapper.updateAppvPrcssNo */
   UPDATE FCM0019M
   SET APPV_PRCSS_NO = #{appvPrcssNo}
       ,UPD_DT = SYSDATE
       ,UPD_USER_ID = #{userId}
    WHERE CLM_NO = #{clmNo}
