mapper.select:
/* scmActivityFundMapper.selectScmActivityFundList */
SELECT T.CLM_NO, T.MEM_ACC_ID, T.MEM_ACC_NAME, TO_DATE(T.CLM_MONTH, 'YYYYMM') AS CLM_MONTH, T.REQST_DT, 'MYR' AS CUR, T.TOT_AMT, T.APPV_PRCSS_NO, T.APPV_PRCSS_STUS_CODE, T.APPV_PRCSS_STUS, T.APPV_PRCSS_DT FROM (SELECT T1.CLM_NO, T1.MEM_ACC_ID, T2.MEM_ACC_NAME, T1.CLM_MONTH, T1.TOT_AMT, T1.APPV_PRCSS_NO, T4.REQST_DT, Nvl (T4.APPV_PRCSS_STUS, 'T') AS APPV_PRCSS_STUS_CODE, Nvl (( CASE T4.APPV_PRCSS_STUS WHEN 'R' THEN 'Request' WHEN 'P' THEN 'Approve In-Progress' WHEN 'A' THEN 'Approved' WHEN 'J' THEN 'Rejected' END ), 'Temporary Save') AS APPV_PRCSS_STUS, T4.APPV_PRCSS_DT FROM FCM0019M T1 LEFT JOIN FCM0006M T2 ON T1.MEM_ACC_ID = T2.MEM_ACC_ID LEFT JOIN FCM0004M T4 ON T1.APPV_PRCSS_NO = T4.APPV_PRCSS_NO WHERE CLM_TYPE = 'J5'
AND T1.CRT_USER_ID = #{loginUserId}
) T WHERE 1=1
AND T.CLM_MONTH = TO_CHAR(TO_DATE(#{clmMonth}, 'MM/YYYY'), 'YYYYMM')
AND UPPER(T.MEM_ACC_ID) = UPPER(#{memAccId})
AND UPPER(T.CLM_NO) = UPPER(#{clmNo})
AND T.APPV_PRCSS_STUS_CODE IN
#{item}
ORDER BY T.CLM_NO, T.CLM_MONTH, T.REQST_DT

mapper.update:
/* scmActivityFundMapper.updateScmActivityFundExp */
   UPDATE FCM0019M
   SET COST_CENTR = UPPER(#{costCentr})
       ,COST_CENTR_NAME = NVL(#{costCentrName}, (SELECT COST_CENTER_TEXT FROM FCM0008M WHERE COST_CENTER = UPPER(#{costCentr})))
       ,MEM_ACC_ID = UPPER(#{memAccId})
       ,BANK_CODE = #{bankCode}
       ,BANK_ACC_NO = #{bankAccNo}
       ,CLM_MONTH = TO_CHAR(TO_DATE(#{clmMonth}, 'MM/YYYY'), 'YYYYMM')
       ,TOT_AMT = #{allTotAmt}
       ,UPD_DT = SYSDATE
       ,UPD_USER_ID = #{userId}
    WHERE CLM_NO = #{clmNo}

/* scmActivityFundMapper.updateScmActivityFundExpItem */
   UPDATE FCM0020D
   SET INVC_DT = TO_DATE(#{invcDt}, 'DD/MM/YYYY')
       ,INVC_TYPE = #{invcType}
       ,INVC_NO = #{invcNo}
       ,SUPPLIR = #{supplirName}
       ,GST_RGIST_NO = #{gstRgistNo}
       ,INVC_REM = #{invcRem}
       ,EXP_TYPE = #{expType}
       ,EXP_TYPE_NAME = #{expTypeName}
       ,GL_ACC_CODE = #{glAccCode}
       ,GL_ACC_CODE_NAME = #{glAccCodeName}
       ,BUDGET_CODE = #{budgetCode}
       ,BUDGET_CODE_NAME = #{budgetCodeName}
       ,TAX_CODE = #{taxCode}
       ,NET_AMT = #{gstBeforAmt}
       ,TAX_AMT = #{gstAmt}
       ,TAX_NON_CLM_AMT = #{taxNonClmAmt}
       ,TOT_AMT = #{totAmt}
       ,EXP_DESC = #{expDesc}
       ,UPD_DT = SYSDATE
       ,UPD_USER_ID = #{userId}
    WHERE CLM_NO = #{clmNo}
    AND CLM_SEQ = #{clmSeq}

mapper.insert:
/* scmActivityFundMapper.insertApproveItems */

   INSERT INTO FCM0015D
   (
        APPV_PRCSS_NO
       ,APPV_ITM_SEQ
       ,INVC_NO
       ,INVC_DT
       ,INVC_TYPE
       ,MEM_ACC_ID
       ,SUPPLIR
       ,PAY_DUE_DT
       ,EXP_TYPE
       ,EXP_TYPE_NAME
       ,COST_CENTR
       ,COST_CENTR_NAME
       ,GL_ACC_CODE
       ,GL_ACC_CODE_NAME
       ,BUDGET_CODE
       ,BUDGET_CODE_NAME
       ,TAX_CODE
       ,NET_AMT
       ,TAX_AMT
       ,TAX_NON_CLM_AMT
       ,APPV_AMT
       ,EXP_DESC
       ,ATCH_FILE_GRP_ID
       ,CLAM_UN
       ,CRT_DT
       ,CRT_USER_ID
       ,UPD_DT
       ,UPD_USER_ID
    )
    values
    (
        #{appvPrcssNo}
       ,#{clmSeq}
       ,#{invcNo}
       ,NVL(TO_DATE(#{invcDt}, 'DD/MM/YYYY'), SYSDATE)
       ,#{invcType}
       ,#{memAccId}
       ,#{supplirName}
       ,TO_DATE(#{payDueDt}, 'DD/MM/YYYY')
       ,#{expType}
       ,#{expTypeName}
       ,#{costCentr}
       ,#{costCentrName}
       ,#{glAccCode}
       ,#{glAccCodeName}
       ,#{budgetCode}
       ,#{budgetCodeName}
       ,#{taxCode}
       ,#{gstBeforAmt}
       ,#{gstAmt}
       ,#{taxNonClmAmt}
       ,#{totAmt}
       ,#{expDesc}
       ,#{atchFileGrpId}
       ,#{clamUn}
       ,SYSDATE
       ,#{userId}
       ,SYSDATE
       ,#{userId}
    )

mapper.update:
/* scmActivityFundMapper.updateAppvPrcssNo */
   UPDATE FCM0019M
   SET APPV_PRCSS_NO = #{appvPrcssNo}
       ,UPD_DT = SYSDATE
       ,UPD_USER_ID = #{userId}
    WHERE CLM_NO = #{clmNo}

UPDATE FCM0020D
SET CLAM_UN = #{newClamUn}
WHERE CLM_NO = #{clmNo}
and CLAM_UN = #{oldClamUn}
