       ,#{costCentr}
       ,#{costCentrName}
       ,#{memAccId}
       ,#{bankCode}
       ,#{bankAccNo}
       ,TO_CHAR(TO_DATE(#{clmMonth}, 'MM/YYYY'), 'YYYYMM')
       ,#{allTotAmt}
       ,#{expGrp}
       ,#{appvPrcssNo}
       ,SYSDATE
       ,#{userId}
       ,SYSDATE
       ,#{userId}
    )

