mapper.update:
/* smGmClaimMapper.updateSmGmClaimExp */
   UPDATE FCM0019M
   SET COST_CENTR = #{costCentr}
       ,COST_CENTR_NAME = #{costCentrName}
       ,MEM_ACC_ID = #{memAccId}
       ,BANK_CODE = #{bankCode}
       ,BANK_ACC_NO = #{bankAccNo}
       ,CLM_MONTH = TO_CHAR(TO_DATE(#{clmMonth}, 'MM/YYYY'), 'YYYYMM')
       ,TOT_AMT = #{allTotAmt}
       ,UPD_DT = SYSDATE
       ,UPD_USER_ID = #{userId}
    WHERE CLM_NO = #{clmNo}

/* smGmClaimMapper.updateSmGmClaimExpItem */
   UPDATE FCM0020D
   SET INVC_DT = TO_DATE(#{invcDt}, 'DD/MM/YYYY')
       ,INVC_TYPE = #{invcType}
       ,INVC_NO = #{invcNo}
       ,SUPPLIR = #{supplirName}
       ,GST_RGIST_NO = #{gstRgistNo}
       ,INVC_REM = #{invcRem}
       ,EXP_TYPE = #{expType}
       ,EXP_TYPE_NAME = #{expTypeName}
       ,GL_ACC_CODE = #{glAccCode}
       ,GL_ACC_CODE_NAME = #{glAccCodeName}
       ,BUDGET_CODE = #{budgetCode}
       ,BUDGET_CODE_NAME = #{budgetCodeName}
       ,TAX_CODE = #{taxCode}
       ,NET_AMT = #{gstBeforAmt}
       ,TAX_AMT = #{gstAmt}
       ,TAX_NON_CLM_AMT = #{taxNonClmAmt}
       ,TOT_AMT = #{totAmt}
       ,EXP_DESC = #{expDesc}
       ,UPD_DT = SYSDATE
       ,UPD_USER_ID = #{userId}
    WHERE CLM_NO = #{clmNo}     
    AND CLM_SEQ = #{clmSeq}

/* smGmClaimMapper.updateSmGmClaimExpMileage */
   UPDATE FCM0021D
   SET CAR_MILAG_DT = TO_DATE(#{carMilagDt}, 'YYYY/MM/DD')
       ,LOC_FROM = #{locFrom}
       ,LOC_TO = #{locTo}
       ,CAR_MILAG = #{carMilag}
       ,CAR_MILAG_AMT = #{carMilagAmt}
       ,TOLL_AMT = #{tollAmt}
       ,PARKING_AMT = #{parkingAmt}
       ,PURPOSE = #{purpose}
       ,UPD_DT = SYSDATE
       ,UPD_USER_ID = #{userId}
    WHERE CLM_NO = #{clmNo}     
    AND CLM_SEQ = #{clmSeq}

