mapper.select:
SELECT POST_DATE, CLM_NO, ADV_TYPE, PAYEE, PAYEE_NAME, COST_CENTER, COST_CENTER_NM, AMT, APPV_PRCSS_NO, APPV_DT, RQST_DT, APPV_PRCSS_STUS, CASE WHEN APPV_PRCSS_STUS = 'A' THEN 'Approved' WHEN APPV_PRCSS_STUS = 'J' THEN 'Rejected' WHEN APPV_PRCSS_STUS = 'R' THEN 'Submitted' WHEN APPV_PRCSS_STUS = 'P' THEN 'Pending' ELSE 'Draft' END AS APPV_PRCSS_STUS_DESC, REPAY_STUS, CASE WHEN REPAY_STUS = '1' THEN 'Not due' WHEN REPAY_STUS = '2' THEN 'Due but not repaid yet' WHEN REPAY_STUS = '3' THEN 'Repaid' WHEN REPAY_STUS = '4' THEN 'Pending Approval' WHEN REPAY_STUS = '5' THEN 'Draft' WHEN REPAY_STUS = '99' THEN '' END AS REPAY_STUS_DESC, ADV_OCC, ADV_OCC_DESC ,IS_RESUBMIT_ALLOW FROM ( SELECT A.CRT_DT POST_DATE, A.CLM_NO, A.ADV_TYPE, A.MEM_ACC_ID PAYEE, F.MEM_ACC_NAME PAYEE_NAME, A.COST_CENTER, A.COST_CENTER_NM, (SELECT SUM(APPV_AMT) FROM FCM0015D WHERE APPV_PRCSS_NO = B.APPV_PRCSS_NO) AS AMT, B.APPV_PRCSS_NO, B.APPV_PRCSS_DT APPV_DT, A.ADV_REFD_DT RQST_DT, NVL(B.APPV_PRCSS_STUS, 'T') APPV_PRCSS_STUS, CASE WHEN A.ADV_TYPE = '3' THEN ( CASE WHEN A.APPV_PRCSS_NO IS NOT NULL THEN (
CASE WHEN (B.APPV_PRCSS_STUS = 'A' AND C.CLM_NO IS NULL AND SYSDATE <= A.ADV_REFD_DT) OR /*(B.APPV_PRCSS_STUS = 'A' AND C.CLM_NO IS NOT NULL AND C.APPV_PRCSS_NO IS NULL AND SYSDATE <= A.ADV_REFD_DT) OR*/ (B.APPV_PRCSS_STUS = 'A' AND C.CLM_NO IS NOT NULL AND D.APPV_PRCSS_STUS = 'J' AND SYSDATE <= A.ADV_REFD_DT) /*THEN 'Not due'*/ THEN '1' WHEN (B.APPV_PRCSS_STUS = 'A' AND D.APPV_PRCSS_STUS = 'A') OR (B.APPV_PRCSS_STUS = 'A' AND A.ADV_REFD_NO = 'MANUAL') /*THEN 'Repaid'*/ THEN '3' WHEN (B.APPV_PRCSS_STUS = 'A' AND C.CLM_NO IS NULL AND SYSDATE >= A.ADV_REFD_DT) OR /*(B.APPV_PRCSS_STUS = 'A' AND C.CLM_NO IS NOT NULL AND C.APPV_PRCSS_NO IS NULL AND SYSDATE >= A.ADV_REFD_DT) OR*/ (B.APPV_PRCSS_STUS = 'A' AND C.CLM_NO IS NOT NULL AND D.APPV_PRCSS_STUS = 'J' AND SYSDATE >= A.ADV_REFD_DT) /*THEN 'Due but not repaid yet'*/ THEN '2' WHEN (B.APPV_PRCSS_STUS = 'A' AND D.APPV_PRCSS_STUS = 'R') OR (B.APPV_PRCSS_STUS = 'A' AND D.APPV_PRCSS_STUS = 'P') /*THEN 'Pending Approval'*/ THEN '4' WHEN (B.APPV_PRCSS_STUS = 'A' AND C.CLM_NO IS NOT NULL AND C.APPV_PRCSS_NO IS NULL AND SYSDATE <= A.ADV_REFD_DT) OR (B.APPV_PRCSS_STUS = 'A' AND C.CLM_NO IS NOT NULL AND C.APPV_PRCSS_NO IS NULL AND SYSDATE >= A.ADV_REFD_DT) /*THEN 'Draft'*/ THEN '5' WHEN B.APPV_PRCSS_STUS = 'R' OR B.APPV_PRCSS_STUS = 'P' OR B.APPV_PRCSS_STUS = 'J' THEN '99' END
) WHEN A.APPV_PRCSS_NO IS NULL AND A.ADV_REFD_NO = 'MANUAL' THEN '3' END ) ELSE '99' END as REPAY_STUS, A.ADV_OCC, E.CODE_DESC ADV_OCC_DESC , NVL(B.IS_RESUBMIT_ALLOWED, 0) AS IS_RESUBMIT_ALLOW FROM FCM0027M A LEFT JOIN FCM0004M B ON A.APPV_PRCSS_NO = B.APPV_PRCSS_NO LEFT JOIN FCM0027M C ON A.ADV_REFD_NO = C.CLM_NO LEFT JOIN FCM0004M D ON C.APPV_PRCSS_NO = D.APPV_PRCSS_NO LEFT JOIN SYS0013M E ON E.CODE_ID = A.ADV_OCC LEFT JOIN FCM0006M F ON F.MEM_ACC_ID = A.MEM_ACC_ID WHERE 1=1
AND A.ADV_TYPE IN
#{item}
AND A.COST_CENTER = #{listCostCenter}
AND A.MEM_ACC_ID = #{memAccCode}
AND A.CRT_DT >= TO_DATE(#{reqStartDt}, 'DD/MM/YYYY')
AND A.CRT_DT < TO_DATE(#{reqEndDt}, 'DD/MM/YYYY') + 1
AND A.CLM_NO >= #{clmNoStart}
AND A.CLM_NO <= #{clmNoEnd}
AND B.APPV_PRCSS_DT >= TO_DATE(#{appStartDt}, 'DD/MM/YYYY')
AND B.APPV_PRCSS_DT <= TO_DATE(#{appEndDt}, 'DD/MM/YYYY') + 1
AND A.CRT_USER_ID = #{loginUserId}
) WHERE 1 = 1
AND REPAY_STUS IN
#{item}
AND APPV_PRCSS_STUS IN
#{item}
ORDER BY POST_DATE DESC

SELECT #{clmType} || NVL (TO_CHAR (MAX (SUBSTR (CLAM_UN, 3, 7) + 1), 'FM0000000'), '0000001') AS CLAM_UN
FROM   FCM0022M
WHERE  CLM_TYPE = #{clmType}

mapper.update:
