WHERE CLM_NO = #{clmNo}

UPDATE FCM0027M
SET APPV_PRCSS_NO = #{appvPrcssNo}
WHERE CLM_NO = #{clmNo}

UPDATE FCM0027M SET
ADV_REFD_NO = #{clmNo}
WHERE CLM_NO = #{advReqClmNo}

mapper.select:
SELECT ADV_TYPE
FROM FCM0027M
WHERE APPV_PRCSS_NO = #{appvPrcssNo}

mapper.update:
UPDATE FCM0027M
SET ADV_REFD_NO = 'MANUAL',
    ADV_REFD_DT = SYSDATE,
    UPD_DT = SYSDATE,
    UPD_USER_ID = #{userId}
WHERE CLM_NO = #{clmNo}
