mapper.select:
/* staffClaimMapper.selectStaffClaimListForExcel */
SELECT * FROM ( SELECT T.CLM_NO, T.COST_CENTR, T.COST_CENTR_NAME, T.MEM_ACC_ID, T.MEM_ACC_NAME, TO_DATE(T.CLM_MONTH, 'YYYYMM') AS CLM_MONTH, T.REQST_DT, 'MYR' AS CUR, T.TOT_AMT, T.APPV_PRCSS_NO, T.APPV_PRCSS_STUS_CODE, T.APPV_PRCSS_STUS, T.APPV_PRCSS_DT, T.IS_RESUBMIT_ALLOWED FROM (SELECT T1.CLM_NO, T1.COST_CENTR, T1.COST_CENTR_NAME, T1.MEM_ACC_ID, T2.MEM_ACC_NAME, T1.CLM_MONTH, T1.TOT_AMT, T1.APPV_PRCSS_NO, T4.REQST_DT, Nvl (T4.APPV_PRCSS_STUS, 'T') AS APPV_PRCSS_STUS_CODE, Nvl (( CASE T4.APPV_PRCSS_STUS WHEN 'R' THEN 'Request' WHEN 'P' THEN 'Approve In-Progress' WHEN 'A' THEN 'Approved' WHEN 'J' THEN 'Rejected' END ), 'Temporary Save') AS APPV_PRCSS_STUS, T4.APPV_PRCSS_DT, T4.IS_RESUBMIT_ALLOWED FROM FCM0019M T1 LEFT JOIN FCM0006M T2 ON T1.MEM_ACC_ID = T2.MEM_ACC_ID LEFT JOIN FCM0004M T4 ON T1.APPV_PRCSS_NO = T4.APPV_PRCSS_NO WHERE CLM_TYPE = 'J4'
AND T1.CRT_USER_ID = #{loginUserId}
) T WHERE 1=1
AND TO_DATE(T.CLM_MONTH,'YYYYMM') BETWEEN TO_DATE(#{clmMonthFrom}, 'MM/YYYY') AND TO_DATE(#{clmMonthTo}, 'MM/YYYY')
AND TO_DATE(TO_CHAR(T.REQST_DT,'DD/MM/YYYY'),'DD/MM/YYYY') BETWEEN TO_DATE(#{requestDateFrom}, 'DD/MM/YYYY') AND TO_DATE(#{requestDateTo}, 'DD/MM/YYYY')
AND UPPER(T.COST_CENTR) IN
UPPER(#{item})
AND UPPER(T.MEM_ACC_ID) IN
UPPER(#{item})
AND T.APPV_PRCSS_STUS_CODE IN
#{item}
ORDER BY T.CLM_NO, T.CLM_MONTH, T.REQST_DT) A LEFT JOIN FCM0020D B ON A.CLM_NO = B.CLM_NO
