mapper.select:
SELECT RES.* FROM ( SELECT X1.*, CASE WHEN X2.APPV_PRCSS_STUS = 'R' THEN 'Request' WHEN X2.APPV_PRCSS_STUS = 'A' THEN 'Approved' WHEN X2.APPV_PRCSS_STUS = 'P' THEN 'Pending' WHEN X2.APPV_PRCSS_STUS = 'J' THEN 'Rejected' WHEN X2.APPV_PRCSS_STUS = 'T' THEN 'DRAFT' WHEN X1.FLG = 'R' THEN 'DRAFT' WHEN X1.FLG = 'M' THEN '' END APPV_PRCSS_STUS, NVL(X2.APPV_PRCSS_STUS, 'T') AS APPV_PRCSS_STUS_CODE, TO_CHAR(X2.REQST_DT, 'DD/MM/YYYY') REQUEST_DT, TO_CHAR(X2.APPV_PRCSS_DT, 'DD/MM/YYYY') APPV_PRCSS_DT, CASE WHEN X2.REQST_USER_ID IS NOT NULL THEN X2.REQST_USER_ID || ' - ' || X3.USER_FULL_NAME ELSE '' END REQUESTOR FROM ( SELECT CASE WHEN B.VENDOR_REQ_NO IS NULL THEN 'M' ELSE 'R' END FLG, B.COST_CENTER, B.COST_CENTER_NAME, NVL(B.VENDOR_REQ_NO, '') REQ_NO, NVL(A.VENDOR_ACC_ID, B.VENDOR_ACC_ID) VENDOR_ACC_ID, NVL(A.VENDOR_GRP, B.VENDOR_GRP) VENDOR_GRP, CASE WHEN B.VENDOR_REQ_NO IS NULL THEN NVL(A.VENDOR_NAME, B.VENDOR_NAME) ELSE NVL(B.VENDOR_NAME, A.VENDOR_NAME) END VENDOR_NAME, NVL(A.VENDOR_REG_NO_NRIC, B.VENDOR_REG_NO_NRIC) VENDOR_REG_NO_NRIC, NVL(B.APPV_PRCSS_NO, '') APPV_PRCSS_NO, B.CRT_USER_ID USER_ID, NVL(A.VENDOR_TYPE, B.VENDOR_TYPE) VENDOR_TYPE FROM FCM0031D A FULL JOIN ( SELECT B1.* FROM FCM0029D B1 JOIN ( SELECT MAX(VENDOR_REQ_NO) REQ_NO FROM FCM0029D GROUP BY VENDOR_REQ_NO ) B2 ON B1.VENDOR_REQ_NO = B2.REQ_NO ) B ON A.VENDOR_REG_NO_NRIC = B.VENDOR_REG_NO_NRIC AND A.VENDOR_ACC_ID = B.VENDOR_ACC_ID WHERE 1=1 /* Vendor related where clause */
AND (NVL(A.VENDOR_ACC_ID, B.VENDOR_ACC_ID)) = #{memAccId} AND (NVL(A.VENDOR_REG_NO_NRIC, B.VENDOR_REG_NO_NRIC)) = #{regNo} AND (NVL(B.COST_CENTER, '')) = #{costCenter}
AND B.CRT_DATE >= TO_DATE(#{startDt}, 'DD/MM/YYYY')
AND B.CRT_DATE < TO_DATE(#{endDt}, 'DD/MM/YYYY') + 1
AND UPPER(B.VENDOR_REQ_NO) >= UPPER(#{vendorReqNoFrom})
AND UPPER(B.VENDOR_REQ_NO) <= UPPER(#{vendorReqNoTo})
) X1 LEFT JOIN FCM0004M X2 ON X1.APPV_PRCSS_NO = X2.APPV_PRCSS_NO LEFT JOIN SYS0047M X3 ON X2.REQST_USER_ID = X3.USER_NAME WHERE 1 = 1
AND VENDOR_TYPE IN
#{item}
/* Approval related where clause */
AND X2.APPV_PRCSS_DT >= TO_DATE(#{appStartDt}, 'DD/MM/YYYY')
AND X2.APPV_PRCSS_DT < TO_DATE(#{appEndDt}, 'DD/MM/YYYY') + 1
) RES
WHERE RES.APPV_PRCSS_STUS_CODE IN
#{item}
WHERE RES.APPV_PRCSS_STUS_CODE IN
#{item}
AND RES.FLG = 'R'

        SELECT DISTINCT A.VENDOR_ACC_ID
        FROM FCM0029D A
		LEFT JOIN FCM0031D B ON A.VENDOR_REG_NO_NRIC = B.VENDOR_REG_NO_NRIC AND A.VENDOR_ACC_ID = B.VENDOR_ACC_ID
		LEFT JOIN FCM0004M C on C.APPV_PRCSS_NO = A.APPV_PRCSS_NO
		WHERE A.VENDOR_REG_NO_NRIC = #{regCompNo}
			AND A.PAY_TYPE = #{paymentMethod}
			AND A.BANK = #{bankList}
			AND A.BANK_ACC_NO = #{bankAccNo}
			AND C.APPV_PRCSS_STUS = 'A'

