mapper.select:
SELECT
    USER_NAME
FROM SYS0047M
WHERE

    HR_CODE = #{memCode}


    HR_CODE = (
        SELECT
            APPV_LINE_USER_ID
        FROM FCM0005D
        WHERE APPV_PRCSS_NO = #{appvPrcssNo}
    AND APPV_LINE_SEQ = #{appvLineSeq}
    )

SELECT COUNT(*)
FROM FCM0029D
WHERE VENDOR_REG_NO_NRIC = #{regCompNo}

mapper.update:
UPDATE FCM0029D
SET APPV_PRCSS_NO = #{appvPrcssNo}
    ,UPD_DATE = SYSDATE
    ,UPD_USER_ID = #{userId}
 WHERE VENDOR_REQ_NO = #{newReqNo}

UPDATE FCM0029D
SET VENDOR_NAME = #{regCompName}
       ,VENDOR_GRP = #{vendorGroup}
       ,PAY_ADV_EMAIL1 = #{payAdvEmail1}
       ,PAY_ADV_EMAIL2 = NVL(#{payAdvEmail2}, NULL)
       ,ADD_HOUSE_LOT_NO = #{houseNo}
       ,ADD_STREET = #{street}
       ,ADD_POSTAL_CODE = #{postalCode}
       ,ADD_CITY = #{city}
       ,ADD_COUNTRY = #{vendorCountry}
       ,BANK_COUNTRY = #{bankCountry}
       ,BANK = #{bankList}
       ,BANK_ACC_HOLDER = #{bankAccHolder}
       ,BANK_ACC_NO = #{bankAccNo}
       ,BANK_BRANCH = #{bankBranch}
       ,SWIFT_CODE = #{swiftCode}
       ,CONTACT_DESIGNATION = #{designation}
       ,CONTACT_NAME = #{vendorName}
       ,CONTACT_PHONE_NO = #{vendorPhoneNo}
       ,CONTACT_EMAIL = #{vendorEmail}
       ,PAY_TERM = #{paymentTerms}
       ,PAY_TYPE = #{paymentMethod}
       ,PAY_OTH = NVL(#{others},NULL)
       ,ATCH_FILE_GRP_ID = #{atchFileGrpId}
       ,APPV_PRCSS_NO = #{appvPrcssNo}
       ,UPD_USER_ID = #{userId}
       ,UPD_DATE = SYSDATE
WHERE VENDOR_REQ_NO = #{newReqNo}

mapper.select:
SELECT RES.* FROM ( SELECT X1.*, CASE WHEN X2.APPV_PRCSS_STUS = 'R' THEN 'Request' WHEN X2.APPV_PRCSS_STUS = 'A' THEN 'Approved' WHEN X2.APPV_PRCSS_STUS = 'P' THEN 'Pending' WHEN X2.APPV_PRCSS_STUS = 'J' THEN 'Rejected' WHEN X2.APPV_PRCSS_STUS = 'T' THEN 'DRAFT' WHEN X1.FLG = 'R' THEN 'DRAFT' WHEN X1.FLG = 'M' THEN '' END APPV_PRCSS_STUS, NVL(X2.APPV_PRCSS_STUS, 'T') AS APPV_PRCSS_STUS_CODE, TO_CHAR(X2.REQST_DT, 'DD/MM/YYYY') REQUEST_DT, TO_CHAR(X2.APPV_PRCSS_DT, 'DD/MM/YYYY') APPV_PRCSS_DT, CASE WHEN X2.REQST_USER_ID IS NOT NULL THEN X2.REQST_USER_ID || ' - ' || X3.USER_FULL_NAME ELSE '' END REQUESTOR FROM ( SELECT NVL(B.VENDOR_REQ_NO, '') REQ_NO, TO_CHAR(B.CRT_DATE, 'DD/MM/YYYY') KEY_IN_DT, NVL(A.VENDOR_GRP, B.VENDOR_GRP) VENDOR_GRP, B.VENDOR_TYPE VENDOR_TYPE, B.COST_CENTER, B.COST_CENTER_NAME, NVL(A.VENDOR_ACC_ID, B.VENDOR_ACC_ID) VENDOR_ACC_ID,
CASE WHEN B.VENDOR_REQ_NO IS NULL THEN NVL(A.VENDOR_NAME, B.VENDOR_NAME) ELSE NVL(B.VENDOR_NAME, A.VENDOR_NAME) END VENDOR_NAME, NVL(A.VENDOR_REG_NO_NRIC, B.VENDOR_REG_NO_NRIC) VENDOR_REG_NO_NRIC, B.PAY_ADV_EMAIL1 EMAIL, B.PAY_ADV_EMAIL2 EMAIL2, CASE WHEN B.VENDOR_REQ_NO IS NULL THEN 'M' ELSE 'R' END FLG, NVL(B.APPV_PRCSS_NO, '') APPV_PRCSS_NO FROM FCM0031D A FULL JOIN ( SELECT B1.* FROM FCM0029D B1 JOIN ( SELECT MAX(VENDOR_REQ_NO) REQ_NO FROM FCM0029D GROUP BY VENDOR_REQ_NO ) B2 ON B1.VENDOR_REQ_NO = B2.REQ_NO ) B ON A.VENDOR_REG_NO_NRIC = B.VENDOR_REG_NO_NRIC AND A.VENDOR_ACC_ID = B.VENDOR_ACC_ID WHERE 1=1 /* Vendor related where clause */
AND (NVL(A.VENDOR_ACC_ID, B.VENDOR_ACC_ID)) = #{memAccId} AND (NVL(A.VENDOR_REG_NO_NRIC, B.VENDOR_REG_NO_NRIC)) = #{regNo} AND (NVL(B.COST_CENTER, '')) = #{costCenter}
AND B.CRT_DATE >= TO_DATE(#{startDt}, 'DD/MM/YYYY')
AND B.CRT_DATE < TO_DATE(#{endDt}, 'DD/MM/YYYY') + 1
AND UPPER(B.VENDOR_REQ_NO) >= UPPER(#{vendorReqNoFrom})
AND UPPER(B.VENDOR_REQ_NO) <= UPPER(#{vendorReqNoTo})
AND B.VENDOR_TYPE IN
#{item}
) X1 LEFT JOIN FCM0004M X2 ON X1.APPV_PRCSS_NO = X2.APPV_PRCSS_NO LEFT JOIN SYS0047M X3 ON X2.REQST_USER_ID = X3.USER_NAME WHERE 1 = 1 /* Approval related where clause */
AND X2.APPV_PRCSS_DT >= TO_DATE(#{appStartDt}, 'DD/MM/YYYY')
AND X2.APPV_PRCSS_DT < TO_DATE(#{appEndDt}, 'DD/MM/YYYY') + 1
) RES
WHERE RES.APPV_PRCSS_STUS_CODE IN
#{item}
WHERE RES.APPV_PRCSS_STUS_CODE IN
#{item}
AND RES.FLG = 'R'
