mapper.insert:
/* webInvoiceMapper.insertWebInvoiceInfo */
   INSERT INTO FCM0002M
   (
        CLM_NO
       ,CLM_TYPE
       ,COST_CENTR
       ,COST_CENTR_NAME
       ,MEM_ACC_ID
       ,GST_RGIST_NO
       ,BANK_CODE
       ,BANK_ACC_NO
       ,INVC_TYPE
       ,INVC_NO
       ,INVC_DT
       ,PAY_DUE_DT
       ,ATCH_FILE_GRP_ID
       ,INVC_REM
       ,TOT_AMT
       ,APPV_PRCSS_NO
       ,CRT_DT
       ,CRT_USER_ID
       ,UPD_DT
       ,UPD_USER_ID
    )
    values
    (
        #{clmNo}
       ,'J1'
       ,#{costCentr}
       ,#{costCentrName}
       ,#{memAccId}
       ,#{gstRgistNo}
       ,#{bankCode}
       ,#{bankAccNo}
       ,#{invcType}
       ,#{invcNo}
       ,TO_DATE(#{invcDt}, 'DD/MM/YYYY')
       ,TO_DATE(#{payDueDt}, 'DD/MM/YYYY')
       ,#{atchFileGrpId}
       ,#{invcRem}
       ,#{totAmt}
       ,#{appvPrcssNo}
       ,SYSDATE
       ,#{crtUserId}
       ,SYSDATE
       ,#{crtUserId}
    )

/* webInvoiceMapper.insertWebInvoiceDetail */

