mapper.select:
/* webInvoiceMapper.selectSupplier */ SELECT ACC_GRP, ACC_GRP_NAME, MEM_ACC_ID, MEM_ACC_NAME, GST_RGIST_NO, BANK_CODE, BANK_NAME, BANK_ACC_NO FROM FCM0006M WHERE 1=1
AND MEM_ACC_ID LIKE '%' || #{memAccId} || '%'
AND ACC_GRP = #{accGrp}
AND MEM_ACC_NAME LIKE '%' || #{memAccName} || '%'
AND GST_RGIST_NO LIKE '%' || #{gstRgistNo} || '%'

/* webInvoiceMapper.selectCostCenter */ SELECT COST_CENTER, COST_CENTER_TEXT FROM FCM0008M WHERE 1=1
AND COST_CENTER LIKE '%' || #{costCenter} || '%'
AND COST_CENTER_TEXT LIKE '%' || #{costCenterText} || '%'
