mapper.select:
/* webInvoiceMapper.selectSupplier */ SELECT ACC_GRP, ACC_GRP_NAME, MEM_ACC_ID, MEM_ACC_NAME, GST_RGIST_NO, BANK_CODE, BANK_NAME, BANK_ACC_NO FROM FCM0006M T1
JOIN (SELECT A.MEM_CODE FROM ORG0001D A LEFT JOIN ORG0005D B ON B.MEM_ID = A.MEM_ID WHERE A.MEM_TYPE =1 AND B.MEM_LVL IN ( 1,2,3) ) T2 ON T2.MEM_CODE = SUBSTR(T1.MEM_ACC_ID,3,6)
WHERE 1=1
AND UPPER(MEM_ACC_ID) = UPPER(#{memAccId})
AND ACC_GRP = #{accGrp}
AND UPPER(MEM_ACC_NAME) LIKE UPPER(#{memAccName}) || '%'
AND GST_RGIST_NO = #{gstRgistNo}

/* webInvoiceMapper.selectCostCenter */ SELECT COST_CENTER, COST_CENTER_TEXT FROM FCM0008M WHERE 1=1
AND UPPER(COST_CENTER) = UPPER(#{costCenter})
AND UPPER(COST_CENTER_TEXT) LIKE '%' || UPPER(#{costCenterText}) || '%'

/* webInvoiceMapper.selectNextClmNo */ SELECT 'J1' || TO_CHAR (SYSDATE, 'yymm') || MAX(CLM_NO) AS CLM_NO FROM ( SELECT NVL (TO_CHAR (MAX (SUBSTR (CLM_NO, 7, 4) + 1), 'FM0000'), '0001') AS CLM_NO FROM FCM0002M WHERE SUBSTR (CLM_NO, 3, 4) = TO_CHAR (SYSDATE, 'yymm')
UNION ALL
SELECT NVL (TO_CHAR (MAX (SUBSTR (CLM_NO, 7, 4) + 1), 'FM0000'), '0001') AS CLM_NO FROM FCM0026D WHERE SUBSTR (CLM_NO, 3, 4) = TO_CHAR (SYSDATE, 'yymm') )

