mapper.select:
SELECT * FROM ( SELECT A.APPV_PRCSS_NO, B.APPV_LINE_SEQ, C.VENDOR_REQ_NO CLM_NO, TO_CHAR(A.REQST_DT, 'DD/MM/YYYY') REQST_DT, D.HR_CODE || ' - ' || E.MEM_ACC_NAME REQUESTOR, C.VENDOR_GRP, C.VENDOR_NAME, C.VENDOR_ACC_ID, C.COST_CENTER, CASE B.APPV_STUS WHEN 'R' THEN 'Request' WHEN 'A' THEN 'Approved' WHEN 'J' THEN 'Rejected' END APPV_PRCSS_STUS, B.APPV_STUS APPV_PRCSS_STUS_CODE, A.APPV_PRCSS_STUS APPV_PRCSS_STUS_04, TO_CHAR(B.APPV_DT, 'DD/MM/YYYY') APPV_DT, A.REQST_USER_ID FROM FCM0004M A LEFT JOIN FCM0005D B ON A.APPV_PRCSS_NO = B.APPV_PRCSS_NO
AND B.APPV_LINE_USER_ID = #{memCode}
AND B.APPV_LINE_USER_ID IN ( SELECT APPR_MEM_CODE FROM FCM0023M WHERE APPR_GRP = #{apprGrp} )
JOIN FCM0029D C ON A.APPV_PRCSS_NO = C.APPV_PRCSS_NO JOIN SYS0047M D ON C.CRT_USER_ID = D.USER_ID JOIN FCM0006M E ON D.HR_CODE = E.MEM_ACC_ID WHERE 1=1 AND (A.APPV_PRCSS_STUS = 'R' OR A.APPV_PRCSS_STUS = 'P' OR A.APPV_PRCSS_STUS = 'A' or A.APPV_PRCSS_STUS = 'J') AND (B.APPV_STUS = 'R' OR B.APPV_STUS = 'A' OR B.APPV_STUS = 'J') ) WHERE 1=1
AND TO_DATE(REQST_DT, 'DD/MM/YYYY') >= TO_DATE(#{startDt}, 'DD/MM/YYYY')
AND TO_DATE(REQST_DT, 'DD/MM/YYYY') < TO_DATE(#{endDt}, 'DD/MM/YYYY') + 1
AND VENDOR_ACC_ID = #{memAccId}
AND VENDOR_GRP IN
#{item}
AND REQST_USER_ID = (SELECT USER_NAME FROM SYS0047M WHERE HR_CODE = UPPER(#{createUser}))
AND COST_CENTER = #{costCenter}
/*AND APPV_PRCSS_STUS_04 IN*/ AND APPV_PRCSS_STUS_CODE IN
#{item}
AND CLM_NO >= UPPER(#{clmNoStart})
AND CLM_NO <= UPPER(#{clmNoEnd})
AND TO_DATE(APPV_DT, 'DD/MM/YYYY') >= TO_DATE(#{appStartDt}, 'DD/MM/YYYY')
AND TO_DATE(APPV_DT, 'DD/MM/YYYY') < TO_DATE(#{appEndDt}, 'DD/MM/YYYY') + 1

/* webInvoiceMapper.selectRejectOfAppvPrcssNo */
SELECT REJCT_RESN
FROM   FCM0005D
WHERE  APPV_PRCSS_NO = #{appvPrcssNo} AND APPV_LINE_SEQ = #{appvLineSeq}

